    border: 1px solid #8D00AF !important;
}
    
 .Voting-bar-filling {
    background: #680181 !important;
}
    
.Voting-voted .Voting-bar {
    background: rgba(104,1,129,0.4) !important;
    border: 1px solid #680181 !important;
    color: #D475EB !important;
}

div.post.warn {
    background: none !important;
    background-color: rgba(152,0,0,1.0) !important;
    box-shadow:inset 120px 0px 50px #000000 !important;
    border: 2px dotted  #ff0000 !important;
}

.MainOverview td.Category, .MainOverview td.LastPost, td {
   background-color: #000000 !important;
   border: 1px solid  #680181 !important;
}

