css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forum.bioware.com) {
  /* Notification settings */
  .notification_table .desc { font-size: 8pt!important }
  /* Status-update comment */
  .list_content,
  /* Status-update comment input */
  .input_text,
  /* Forum post, quote */
  .post_body .post,
  /* Forum posts on profile page */
  #profile_panes_wrap .post p,
  /* Post previews */
  #post_preview,
  /* Thread starter, reply count, group quick-menu */
  .ipsType_small, .hentry,
  /* Thread header */
  .ipsBox_withphoto > .desc { font-size: 10pt }
  /* Thread name in listing */
  .__topic a.topic_title,
  /* PM name in listing */
  .col_m_subject a { font-size: 11pt }
  #mainContent {
    background-color: #333;
  }
  #secondary_navigation,
  .topic_buttons > li > a {
    background-color: #929292;
    text-shadow: none;
  }
  #secondary_navigation a {
    color: #ddd;
  }
  a,
  #more_topics a,
  .topic_desc.desc.blend_links a,
  table.ipb_table .col_f_views li > a,
  #secondary_navigation a:hover {
    color: #fff;
  }
  .general_box h3,
  .ipsSideBlock h3 {
    background-color: #666;
    color: #ddd;
  }
  .ipsHeaderMenu a,
  .popupInner a,
  ul.mini_pagination > li > a,
  .ipsLikeBar_info a {
    color: #000;
  }
  .post_body .ipsBlockquote,
  .statistics_head,
  #profile_tabs li,
  #usercp_tabs li,
  .ipbfs_titlebox {
    border-color: #666;
    background-color: #444;
  }
  #profile_panes_wrap .bar,
  #usercp_tabs li.active a,
  #profile_tabs li.active a {
      background-color: #555;
      color: #ddd;
  }
  #statusForm > input {
    vertical-align: middle;
  }
  .topic_buttons > li > a:hover {
    background-color: #aaa;
  }
  #post_preview,
  #profile_panes_wrap .post p,
  .post.entry-content p,
  .post_body .post,
  .status_content,
  .ipsType_pagetitle,
  .ipsType_subtitle,
  .no_messages,
  #mainContent label,
  .notification_table,
  .notification_table .desc,
  .ipsSettings_sectiontitle,
  #usercp_content strong,
  .topic_buttons > li.non_button > a:hover {
    color: #ddd;
  }
  #more_topics {
    border-top: none;
    background-color: #929292;
  }
  #more_topics:hover {
    background-color: #bebebe;
  }
  #mainContent .ipsBox_container {
    background-color: #333;
    border-color: #666;
  }
  #forum_footer,
  #profile_panes_wrap .ipsBlockquote p,
  .pagejump > a,
  .ipsButton_extra,
  .ipsSideMenu h4,
  .ipsType_pagedesc.forum_rules,
  .ipsType_pagedesc,
  .col_f_content > .topic_desc,
  .download_pane h2,
  .topic_buttons > li.non_button > a,
  .col_f_views > ul > li,
  .general_box,
  .post_body .post .ipsBlockquote,
  .post.entry-content .ipsBlockquote p {
    color: #bbb;
  }
  body#ipboard_body fieldset.submit,
  .ipsBox,
  .statistics,
  .row1,
  .row2,
  .post_block,
  .column_view .post_body,
  ul.post_controls,
  .no_messages,
  .general_box,
  .ipsSideBlock {
    background: none;
  }
  .content_border,
  .post_block h3,
  .block_list li,
  table.ipb_table td,
  #forum_table.ipb_table tr:last-of-type > td,
  #footer_utilities,
  div#search_results li.liwrap,
  body#ipboard_body fieldset.submit,
  #usercp_content .ipsType_subtitle,
  .download_pane h2,
  .status_feedback .ipsList_withtinyphoto > li,
  .ipsSideBlock h3,
  .ipsSideMenu > ul,
  .ipsSideMenu > ul > li,
  .ipsList_withminiphoto li,
  .general_box h3,
  .ipsList_data li,
  .signature,
  #profile_tabs > ul,
  #usercp_tabs > ul,
  .ipsSettings_section,
  ul.post_controls {
    border-color: #666;
  }
  ul.post_controls > li > a {
    border-color: #666!important;
    text-shadow: none;
    color: #fff;
  }
  body#ipboard_body p.submit,
  #profile_panes_wrap .bar,
  .post_body .citation {
    background-color: #666;
    border-color: #666;
    text-shadow: none;
    color: #ddd;
  }
  #post_preview {
    border: 1px solid #666;
  }
  .popupInner .ipsBox_container {
    border-color: #dcdcdc;
  }
  .popupInner .block_list li {
    border-color: #eee;
  }
  .ipsHeaderMenu a:hover,
  .popupInner a:hover,
  .ipsLikeBar_info a:hover,
  ul.post_controls > li > a:hover,
  table.ipb_table .col_f_views li > a:hover,
  .topic_desc > a:hover,
  .last_post > .highlight_unread > a:hover {
    color: #A27C6F!important;
  }
}
