css:
/* codemirror/theme/elegant.css */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:")
/* so Stylish popup lists this style on any about: page */ { }

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{

.cm-s-mozilla span.cm-number,
.cm-s-mozilla span.cm-string,
.cm-s-mozilla span.cm-atom {color: #762;}
.cm-s-mozilla span.cm-comment {color: #262; font-style: italic; line-height: 1em;}
.cm-s-mozilla span.cm-meta {color: #555; font-style: italic; line-height: 1em;}
.cm-s-mozilla span.cm-variable {color: black;}
.cm-s-mozilla span.cm-variable-2 {color: #b11;}
.cm-s-mozilla span.cm-variable-3 {/**/}
.cm-s-mozilla span.cm-qualifier {color: #555;}
.cm-s-mozilla span.cm-keyword {color: #730;}
  .cm-s-mozilla span[class="cm-error"],
.cm-s-mozilla span.cm-builtin {color: #30a;}
.-cm-s-mozilla span.cm-error {background-color: #fdd;}
.cm-s-mozilla span.cm-link {color: #762;}

.cm-s-mozilla pre:not(hing) {color: inherit;}
  .cm-s-mozilla span.cm-property.cm-error,
.cm-s-mozilla span.cm-tag {color: inherit;}

}
