	#header{height:auto !important; background:black none !important;}
	#content{width:auto}
	#content-container, body, #footer, .posttop, .post,.postbottom, .postbottom *, h1, h2, h3, 
	#sidebar2, #sidebar2 *{background:black none !important;}
	.p0st-container{background:black none !important; float: right !important;}
	a,h1, h2, h3{font-size:1.5em !important; line-height:1.5em !important;}
	h1, h1 *, h2, h2 *, h3, h3 * {color:red !important;}
	#sb1,#sidebar,img.iN{width:0px !important; display:none !important;}
	.iH a img {display: none !important;}
	.iH, .iH *{background-color:transparent!important;text-shadow: 1px 1px black;}

	.iH img {background-color:transparent !important; opacity: 0.5;}
	#content-container{background-color:black !important}
	.postbottom * {background-color:black !important; color:white !important;}
