css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.im") {
#body, .text {
background-color: rgb(34, 34, 34);
color: rgb(177, 177, 177) !important;
}
body {
background: #333333 !important;
color: rgb(177, 177, 177) !important;
}
.quote, #xsite-nav, #menubar, .error {
background-color: rgb(41, 41, 41) !important;
color: rgb(177, 177, 177) !important; 
border: 1px solid rgb(110, 110, 110) !important;
}
a, .page {
color: rgb(121, 185, 77) !important;
background-color: inherit !important;
}
a:hover {
color: rgb(153, 217, 109) !important;
background-color: inherit !important;
}
.text {
background-color: rgb(17, 17, 17) !important;
}
div.options a, #footer a {
border-bottom: none !important;
}
#body {
padding: 0.3em;
width: auto !Important;
margin-left: 80px !Important;
margin-right: 80px !Important;
border-radius: 2px !important;
border: 2px solid rgb(110, 110, 110) !important;
}
.share {
display: none !important;
}
button, .button, input[type="submit"] {
background: rgb(17, 17, 17) !important;
box-shadow: none !important;
text-shadow: none !important;
color: rgb(177, 177, 177) !important;
border-radius: 2px !important;
border: 1px solid rgb(110, 110, 110) !important;
}
button:hover, .button:hover, div.more a:hover, input[type="submit"]:hover {
color: #ffffff !important;
background: rgb(17, 17, 17) !important;
}
input.page {
background: none !important;
}
input[type="text"] {
background: #333333 !important;
color: rgb(177, 177, 177) !important;
border-radius: 2px !important;
border: 1px solid rgb(110, 110, 110) !important;
}
div#news a {
border-color: #79B94D !important;
}
div#news a:hover {
border-color: rgb(153, 217, 109) !important;
}
}
