css:
@-moz-document domain("soylentnews.org") {
#u2tabs.left_menu li.active, #links.left_menu li.active, #firehose article header .details, #comments article header .details, #comments .full .detail, #modal_box #editor #editpreview .details, #slashboxes article, .daybreak a, .msg p, footer[class="grid_24"], html, body, .col_wrapper, #u2tabs.left_menu li.active > span, #links.left_menu li.active > span
{
    background: #1E1C1C !important;
    color: #D8F1F4 !important;
}
#links div.content,#slashboxes,.generalbody,.commentBody,.commentwrap,.commentTop .details,#links,#navigation-content li.user,#navigation-content ul ul,.article .body,#slashboxes div.content 
{
    background: #1E1C1C !important;
    color: #D8F1F4 !important;
}
.article .details,div.storylinks div ul,.commentSub
{
    background: #3C3C3C !important;
    color: #D8F1F4 !important;
}
#slashboxes h4,#links h4 ,.commentBoxForm,.commentTop .details
{
    background: #3C3C3C !important;
    color: #D8F1F4 !important;
}
.generaltitle h3,h4 
{
    background: #470000 !important;
    color: #BDD3D3 !important;
}
textarea,body, div, form, #links ul li, #links ul, #topnav, img
{
    background: #1E1C1C !important;
    color: #D8F1F4 !important;
}
a {
    color: #81AC82 !important;
}


}
