css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.belastingdienst.nl") {
    
    body{
       background-image : none;
       background-color : #333;
       color : #AAA;
    }
    
    h1, h2, h3, h4, h5, h6, h7, h8{
       color : #AAA;
    }
    a{
       color : #6AD !important;
    }
    
    
    
    /***
    * Background
    ***/
    .container{
       background : none #444;
       box-shadow : 0px 0px 8px #222;
    }
    .content_wrapper1, .content, .header, .header span, .footer, .footer span, .footer .ref{
       background : none;
    }
    
    /***
    * Navbar
    ***/
    .light_blue .nav_bar{
       background-color : #456; 
    }
    .light_blue .sel a{
       background-color : #678;
    }
    .light_blue .content_wrapper2{
       border-top : 10px solid #678;
    }
    
    /* Search box: */
    form input.text, form textarea{
       background-color : #666;
    }
    
    /***
    * Side Bar
    ***/
    .light_blue .nav_sub li.open a, .light_blue .nav_sub li.open li.open a, .light_blue .nav_sub li.open li.open li.open a, .light_blue .nav_sub li.open li.open li.open li.open a, .light_blue .nav_sub li.open li.open li.open li.open li.open a{
        background-color : #567;
    }
    .light_blue .nav_sub li.active a, .light_blue .nav_sub li.open li.active a, .light_blue .nav_sub li.open li.open li.active a, .light_blue .nav_sub li.open li.open li.open li.active a, .light_blue .nav_sub li.open li.open li.open li.open li.active a, .light_blue .nav_sub li.open li.open li.open li.open li.open li.active a{
        background-color : #456;
    }
    .nav_sub .mod.box.open.lighter, .content_add .mod.box.open.lighter{
       background-color : #444;
       border-color : #666;
    }
    
    
}
