css:
@-moz-document domain("kpn.com") 

{

/* 
IE gebruikers kunnen het Stylesheet testen door naar 
Internet Opties >> Toegankelijkheid (accessibility) te gaan,
de css code na de ster schuine streep te kopieeren
in een notepad op te slaan als c:\kpn.css 
invullen in IE opmaakmodel User style sheet,
vink bijhorende optie aan surf vervolgens naar forum.kpn.com.
als je uitgekeken bent haal je het vinkje/css weer weg. 
*/


.ForumListingBoxThreadContent, 
.post_message,
.ForumListingBoxItemDesc { color:red!important;}
.ForumListingBoxItemDesc p {color:navy!important;}

.ForumListingBoxThreadContent .post_message {color: black!important;}

#ForumContentBreadcrumb ul li a, .breadcrumbs li a,
body,
.lia-content,
.lia-content p, 
td,
.post_message,
#pa_mainnav ul.lev2 li a {color:#000 !important;}


#wrapper { background-color:white !important;
  border:1px solid black;
  opacity:.97;
  filter:alpha(opacity=97);}

#pa_footer a,
#ph_footersitemap a {color:navy !important;}

#lia-body .lia-content .message-body-editor textarea {width:100%;}
#lia-body .lia-content .lia-form textarea  {height:300px;width:600px;}

#pa_mainnav:before
{ 
padding-left:28px;
color:#999;
content:"KPN wenst Iedereen Vrolijk Pasen, Zoek de eieren";
font-weight:bold;
}

.DateTime,
.search-count-stats {margin-left:20px;}


#display_1,
.lia-user-avatar-message {
width:auto;
height:auto;
-webkit-transition:width 2s, height 2s, -webkit-transform 2s; /* For Safari 3.1 to 6.0 */
transition:width 2s, height 2s, transform 2s;
}
    
#display_1:hover,
.lia-user-avatar-message:hover {
-webkit-animation-delay: 0s;
-webkit-animation-direction: normal;
-webkit-animation-duration: 5s;
-webkit-animation-fill-mode: none;
-webkit-animation-iteration-count: 1;
-webkit-animation-name: animated_img;
-webkit-animation-play-state: running;
-webkit-animation-timing-function: ease;
width:auto;
height:auto;
-webkit-transform:rotate(365deg); /* Safari */
transform:rotate(365deg);
  }
      

/* litium easter egg test voor firefox */     
#display_2:hover
{
padding-left:20px;
color:red;
content:url('http://www.hellasmultimedia.com/webimages/easter-htm/easter/images/bunnies/ani006.gif');
font-weight:bold;
}
    
    
body {
        background-position: center center!important;
	background: #FFF !important;      
        background: url('http://images3.alphacoders.com/117/117455.jpg') no-repeat fixed center !important;
	-webkit-background-size: cover;
	-moz-background-size: cover;
	-o-background-size: cover;
	-ms-background-size: cover;
	background-size: cover;
    }
 
     
.button-blue {color:#fff !important;}

/* witte achtergrond */  
#header_wrapper,
#tinyMceEditor_ifr,
.block, 
.row { background-color:white !important;opacity:1;filter:alpha(opacity=100) !important;}


#pa_logo:hover {
padding-left:20px;
content:url('http://www.hellasmultimedia.com/webimages/easter-htm/easter/images/bunnies/ani006.gif'); 
}



}
