css:
@namespace url(http://www.w3.org/1999/xhtml);  
@-moz-document domain('habitrpg.com'){
    /*Global settings*/
    body{background-color:#000000 !important;color:#55aaaa !important;}
    
    
    input[type="text"]{background-color:#000 !important;box-shadow:inset 0 0 5px #444 !important}
    input[type="number"]{background-color:#000 !important;box-shadow:inset 0 0 5px #444 !important}
    textarea{background-color:#111 !important;color:#5aa !important;border:none !important;box-shadow:inset 0 0 5px #444 !important}
    .glyphicon{color:#cccccc !important}
    
    a:hover{box-shadow:inset 0 0 5px #cccccc !important}
    a{background-color:transparent !important;border:none !important;color:#55aaaa !important}
    .btn-default{background-color:transparent !important;color:#55aaaa !important;border:none !important;box-shadow:inset 0 0 5px #cccccc !important}
    li{background-color:transparent !important;}
    .dropdown-menu{background-color:#111 !important;box-shadow:inset 0 0 5px #ccc !important}
    .modal-content{background-color:#111 !important;box-shadow:inset 0 0 5px #ccc !important}
    .modal-header{border:none}
    .modal-footer{border:none}
    .well{background-color:#111 !important;border:none !important;box-shadow:inset 0 0 5px #444 !important}
    .herobox.selected-class{outline:none;border:none;box-shadow:inset 0 0 5px #cccccc !important}
    .tooltip-inner{box-shadow:inset 0 0 5px #cccccc !important;color:#55aaaa !important}
    .tooltip-arrow{border-top-color:#555 !important}
    
    /*alerts*/
    .alert-warning{background-color:#622 !important;border:none;color:#55aaaa !important;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-crit{background-color:#664422 !important;border:none;color:#55aaaa !important;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-hp{background-color:#552222 !important;border:none;color:#55aaaa !important;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-mp{background-color:#222266 !important ;border:none;color:#55aaaa;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-xp{background-color:#332233;border:none;color:#55aaaa;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-gp{background-color:#333322;border:none;color:#55aaaa;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-lvl{background-color:#552277;border:none;color:#55aaaa;box-shadow:inset 0 0 5px #cccccc !important}
    .alert-drop{background-color:#335522;border:none;color:#55aaaa;box-shadow:inset 0 0 5px #ccc !important}
    
    

    
    
    /*main page*/
    .navbar-collapse{color:#55aaaa !important}
    .task-options{background-color:#111 !important;color:#aaa !important}
    .option-title{color:#55aaaa !important}
    .addtask-btn{background-color:#000 !important;box-shadow:inset 0 0 5px #444 !important}
    .addtask-btn:hover{box-shadow:inset 0 0 5px #ccc !important;}
    .hero-stats{background-color:#111111 !important;border:none !important;box-shadow:inset 0 0 5px #444444 !important}
    .task-column{background-color:#111111 !important;border:none !important;box-shadow:inset 0 0 5px #444444 !important}
    .task-action-btn{background-color:transparent !important;color:#cccccc !important;}
    
    .color-best  .task-controls{background-color:#013 !important;box-shadow:inset 0 0 5px #234 !important}
    .color-better  .task-controls{background-color:#112 !important;box-shadow:inset 0 0 5px #224 !important}
    .color-good  .task-controls{background-color:#121 !important;box-shadow:inset 0 0 5px #242 !important}
    .color-neutral  .task-controls{background-color:#111 !important;box-shadow:inset 0 0 5px #222 !important}
    .color-bad  .task-controls{background-color:#310 !important;box-shadow:inset 0 0 5px #222 !important}
    .color-worse  .task-controls{background-color:#211 !important;box-shadow:inset 0 0 5px #222 !important}
    .color-worst  .task-controls{background-color:#100 !important;box-shadow:inset 0 0 5px #222 !important}
    
    
    /*Header settings*/
    
    .header-wrap{background-color:#000000 !important;}
    .navbar{background-color:#111111 !important;box-shadow:inset 0 0 5px #444444 !important}
    .herobox{background-color:#090909 !important;box-shadow:inset 0 0 5px #444444 !important}
   
    .meter{background-color:#111111 !important; color:#55bbbb !important;box-shadow:inset 0 0 5px #444444 !important}
    .experience .bar{background-color:#73b !important;box-shadow:inset 0 0 5px #444444 !important}
    .health .bar{background-color:#b33 !important;box-shadow:inset 0 0 5px #444444 !important}
    .mana .bar{background-color:#259 !important;box-shadow:inset 0 0 5px #444444 !important}
    
    .filters a:hover{background-color:#000000 !important;}
    .popover{background-color:#000000 !important;box-shadow:inset 0 0 5px #cccccc !important}
    h3.popover-title{background-color:#333333 !important;border:none !important;}
    .popover.top .arrow:after{border-top-color:#333333 !important}
    .popover.right .arrow:after{border-right-color:#333333 !important}
    .popover.bottom .arrow:after{border-bottom-color:#333333 !important}
    .popover.left .arrow:after{border-left-color:#333333 !important}
    
    
    /*habit colors*/
    
    
    
    /*.task.habit.color-best{background-color:#223355 !important;}*/
    .task.habit.color-better{background-color:#333344 !important;}
    .task.habit.color-good{background-color:#334433 !important;}
    .task.habit.color-neutral{background-color:#333333 !important;}
    .task.habit.color-bad{background-color:#553322 !important;}
    .task.habit.color-worse{background-color:#553333 !important;}
    
    .color-best .task-action-btn{background-color:#112244 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #223355 !important}
    .color-best .task-action-btn:hover{background-color:#334466 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #223355 !important}
    .color-better .task-action-btn{background-color:#222233 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #333344 !important}
    .color-better .task-action-btn:hover{background-color:#444455 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #333344 !important}
    .color-good .task-action-btn{background-color:#223322 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #334433 !important}
    .color-good .task-action-btn:hover{background-color:#445544 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #334433 !important}
    .color-neutral .task-action-btn{background-color:#222222 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #333333 !important}
    .color-neutral .task-action-btn:hover{background-color:#555555 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #333333 !important}
    .color-bad .task-action-btn{background-color:#442211 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #553322 !important}
    .color-bad .task-action-btn:hover{background-color:#664433 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #553322 !important}
    .color-worse .task-action-btn{background-color:#441111 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #553333 !important}
    .color-worse .task-action-btn:hover{background-color:#664444 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #553333 !important}
    .color-worst .task-action-btn{background-color:#440000 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #552222 !important}
    .color-worst .task-action-btn:hover{background-color:#663333 !important;color:#cccccc !important;box-shadow:inset 0 0 5px #552222 !important}
    
    
    
    
    /*daily and todo colors*/
    
    .completed.color-best                     {background-color:#111522 !important; color:#115555 !important;box-shadow:inset 0 0 5px #223355 !important}
    .completed.color-best .action-yesno label {background-color:#111522 !important;box-shadow:inset 0 0 5px #223355 !important}
    .completed.color-better                     {background-color:#111115 !important; color:#115555 !important;box-shadow:inset 0 0 5px #333344 !important}
    .completed.color-better .action-yesno label {background-color:#111115 !important;box-shadow:inset 0 0 5px #333344 !important}
    .completed.color-good                       {background-color:#111511 !important; color:#115555 !important;box-shadow:inset 0 0 5px #334433 !important}
    .completed.color-good .action-yesno label   {background-color:#111511 !important;box-shadow:inset 0 0 5px #334433 !important}
    .completed.color-neutral                    {background-color:#111111 !important; color:#115555 !important;box-shadow:inset 0 0 5px #333333 !important}
    .completed.color-neutral .action-yesno label{background-color:#111111 !important;box-shadow:inset 0 0 5px #333333 !important}
    .completed.color-bad                        {background-color:#221511 !important; color:#115555 !important;box-shadow:inset 0 0 5px #553322 !important}
    .completed.color-bad .action-yesno label    {background-color:#221511 !important;box-shadow:inset 0 0 5px #553322 !important}
    .completed.color-worse                        {background-color:#151111 !important; color:#115555 !important;box-shadow:inset 0 0 5px #553333 !important}
    .completed.color-worse .action-yesno label    {background-color:#151111 !important;box-shadow:inset 0 0 5px #553333 !important}
    .completed.color-worst                        {background-color:#150000 !important; color:#115555 !important;box-shadow:inset 0 0 5px #552222 !important}
    .completed.color-worst .action-yesno label    {background-color:#150000 !important;box-shadow:inset 0 0 5px #552222 !important}
    
    .color-best.uncompleted{background-color:#223355 !important; color:#33bbbb !important}
    .color-best.uncompleted .action-yesno label{background-color:#112244 !important; box-shadow:inset 0 0 5px #223355 !important}/*checkbox bkg*/
    .color-better.uncompleted{background-color:#222244 !important; color:#33bbbb !important}
    .color-better.uncompleted .action-yesno label{background-color:#222233 !important; box-shadow:inset 0 0 5px #333344 !important}/*checkbox bkg*/
    .color-good.uncompleted{background-color:#334433 !important; color:#33bbbb !important}
    .color-good.uncompleted .action-yesno label{background-color:#223322 !important; }/*checkbox bkg*/
    .color-neutral.uncompleted{background-color:#333333 !important; color:#33bbbb !important}
    .color-neutral.uncompleted .action-yesno label{background-color:#222222 !important; box-shadow:inset 0 0 5px #333333 !important}/*checkbox bkg*/
    .color-bad.uncompleted{background-color:#553322 !important; color:#33bbbb !important}
    .color-bad.uncompleted .action-yesno label{background-color:#442211 !important; box-shadow:inset 0 0 5px #553322 !important}/*checkbox bkg*/
    .color-worse.uncompleted{background-color:#552222 !important; color:#33bbbb !important}
    .color-worse.uncompleted .action-yesno label{background-color:#441111 !important; box-shadow:inset 0 0 5px #553333 !important}/*checkbox bkg*/
    .color-worst.uncompleted{background-color:#551111 !important; color:#33bbbb !important}
    .color-worst.uncompleted .action-yesno label{background-color:#440000 !important; box-shadow:inset 0 0 5px #553333 !important}/*checkbox bkg*/
    
    
    .action-yesno label:after{border-color:#fff !important;color:#cccccc !important;}/*changes the checkmark*/
    
    .cast-target:hover{border: 5px dotted #666 !important;}
    
   
    /*rewards*/
    
    .reward-item{background-color:#444 !important}
    .reward-item .task-controls{background-color:#222222 !important;box-shadow:inset 0 0 5px #444 !important}
    
    .task.reward.color-best{background-color:#444 !important}
    .reward.color-best  .task-controls{background-color:#232 !important;box-shadow:inset 0 0 5px #444 !important}
    .task.reward.color-better{background-color:#333 !important}
    .reward.color-better  .task-controls{background-color:#121 !important;box-shadow:inset 0 0 5px #333 !important}
    .task.reward.color-good{background-color:#222 !important}
    .reward.color-good  .task-controls{background-color:#010 !important;box-shadow:inset 0 0 5px #222 !important}
    
    
    /*inventory*/
    .pet-button{background-color:#333 !important}
    .pet-button.selectableInventory:hover{background-color:#353 !important}
    
    /*social screens*/
    li.active{background-color:#111 !important; box-shadow:inset 0 0 5px #444 !important}
    
    .panel{background-color:#111111;border:none;box-shadow:inset 0 0 5px #cccccc !important}
    .panel-heading{background-color:#222222 !important;color:#55aaaa !important;border:none;box-shadow:inset 0 0 5px #cccccc !important}
    .panel-footer{background-color:#222222 !important;color:#55aaaa !important;border:none;box-shadow:inset 0 0 5px #cccccc !important}
    
    select[id="partyOrder"]{background-color:#444 !important;border:none}
    td{background:#111111;border:none !important}
    .table-striped tbody tr:nth-child(2n+1) td{background-color:#222222 !important;border:none !important;margin-top:-1px}
    
    .alert-info{background:#333 !important;border:none}
    .chat-message{border-bottom-color:#333333 !important;}
    .chat-message.highlight{background-color:#444444 !important;}
    code{color:#ccc !important;background:#222 !important}
    
 
     /*challenges*/
    .col-md-2{background:#111111 !important;border:none;box-shadow:inset 0 0 5px #cccccc !important}
    
    
    
    .footer{background-color:#444444 !important;border:none}
}
