css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kafan.cn") {
/* --- 背景色 --- */
body{background:#DCDCDC !important;}
/*-- Hide 1st --*/
#hd .hdc.cl >h2 >a ,.hdc.cl >#um >.avt.y /*Logo&Head*/
,#um > p:nth-child(2) > span:nth-child(2) ,#um > p:nth-child(2) > a:nth-child(3) /*QQBind*/
,#qmenu
,#hd >.wp >.a_mu/*NewAd*/
    {display:none !important;}
/*-- 导航栏 --*/
#hd >.wp >#nv {
position:absolute!important;
width:100%;
border:none !important;
left:0px !important;
top:0px !important;
background:#33373B !important;
}
#hd >.wp >#nv >ul >li{
background-color: #33373B !important;
background-image:none !important;
}
#hd >.wp >#nv >ul >li >a {
color:#C0C0C0 !important;
font-size:13px !important;
border-right:1px solid #555 !important; 
background-image:none !important;
}
/* --- RightSideBar --- */
#um > p:nth-child(2){
position:absolute!important;
top:2px !important;
right:10px !important;
z-index:500 !important;
padding:2px !important;
}
#um >p:nth-child(2) >* , .vwmy >a{ color:#C0C0C0 !important;}
.vwmy{background-image:none !important;}

#um >p:nth-child(3){ margin-right:-115px !important;margin-top:-10px !important;}
/* --- WhiteHeader --- */
#nv_forum >#hd{
margin-bottom:13px !important;
height:45px!important;
background:white!important;
}
/* --- > > > --- */
#wp >#pt{position:absolute;
top:36px !important;
left:0 !important;}
#wp >#pt >.z{padding-left:15px !important;}
#pt.bm.cl >.y ,#pt.bm.cl >.z >#connectlike{display:none ;}
    
/* --- 表格 --- */
.fl_row{border-top:1px solid #DEDEDE !important;}
.bm_h ,.th{
    border:none !important;
    border-bottom:1px solid #CDCDCD!important;
    background:#EEE !important;
}
.bm.bmw ,.th{
    margin-top:-1px !important; 
    border-top:1px solid #CDCDCD !important;
}  
div.bm.bmw.cl:nth-child(1) > div:nth-child(1){    border-top:1px solid #CDCDCD !important;}    
/* --- 版块图标 --- */
/*[[FLICN]]*/
.fl_i ,.fl_by{border-left:1px solid #DEDEDE !important;text-align:center;}
    
/* --- 帖子列表 --- */
td.by >.pstyle >img{display:none !important;} 
tbody:nth-child(2n) {background-color: #FAFAFA !important;}
.tl th ,.tl td{border-bottom:none !important;}  
td.by ,td.num{border-left:1px solid #DEDEDE !important;text-align:center;}
#threadlisttableid > tbody > tr > *{padding-top:1px !important;}/*帖子列表行间距*/

#separatorline{display:none ;}/*隐藏版块主题横条*/
.bm_c >.cl >ul.xl.xl2.cl{padding-left:10px !important;}/*推荐主题调整*/
    
a.s.xst{font-size:13px !important;font-weight:normal !important;}
th > em, th >em>*{font-weight:bold !important;}  
.tdpre.y{
background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAYAAABy6+R8AAAAn0lEQVQokZWS0Q3DMAhEPXvAK5hByKf3INu8fjROk+C06knIEuK4A1zKCRGBu2NmqCqtNdydbdsoM/TeqbWyLMslRIRaK713EuFePIuDGBGoaioAUk5V31bdPdkREQbuRHentNaO4iecSWZGOVv7htFYVSlm9pOUlMZMIpIWARwzXmb6d3sRwfROo/P9nR54pjgUEuHp75kZ67p+LO14AU0Lpm5gT+y7AAAAAElFTkSuQmCC)!important;
background-repeat: no-repeat;
} 
    
/* --- 发帖&回复按钮 --- */

#newspecialtmp > img ,#newspecial > img ,#post_replytmp > img ,#post_reply >img ,#pgt >a >img{
opacity:0;
}
#newspecialtmp,#newspecial{ /*发帖按钮*/
border:1px solid #CDCDCD;
width:70px !important;
height:30px !important;
background:url(data:image/png;base64,) no-repeat !important;
}
#post_replytmp,#post_reply{ /*回复按钮*/
border:1px solid #CDCDCD;
width:70px !important;
height:30px !important;
background:url(data:image/png;base64,) no-repeat !important;
}  
#pgt > a:nth-child(2){ /*导读页面发帖按钮*/
position:absolute !important;
background:url(data:image/png;base64,) no-repeat !important;
margin-top:2px !important;
}
    
/* --- 顶部翻页、发帖 主题分类合并 ---*/
.mn>#pgt.bm{
    padding-right:5px !important;
    border:1px solid #CDCDCD !important;
    background:#EEE!important;
}
#pgt.bm>#newspecialtmp,#pgt.bm>#newspecial{
    border:none !important;
    width:70px !important;
    height:30px !important; 
background:url(data:image/png;base64,) no-repeat !important;
}
#thread_types{
    background:white !important;
    border:none !important;
    margin:-10px 1px 12px 1px !important;
    padding-left:10px !important;
    border-bottom:7px solid white !important;
}
#pgt.bm >#fd_page_top >.pg >strong{
    padding:0 6px !important;
    border:none !important;
    background:#CDCDCD !important;
}
#pgt.bm >span ,#pgt.bm >#fd_page_top >.pg{margin-top:2px !important;}

#pgt.bm >#fd_page_top >.pg >a ,#pgt.bm >#fd_page_top >.pg >label 
,#pgt.bm>#visitedforums>a,#pgt.bm>#visitedforumstmp>a{
    padding:0 5px 2px 5px !important;
    border:none !important;
    background:#EEE;
}  
#pgt.bm >#fd_page_top >.pg >a.prev{
background: url('http://a.ikafan.com/image/common/arw_l.gif?LYD') no-repeat !important;
background-position: 50% 46%!important;
}
    
/* --- 帖子阅读界面 --- */
.pls{background:white !important;border-color:#CDCDCD !important;}
tr.ad>td.pls,tr.ad > td.plc{background:#CDCDCD !important; height:5px !important;}
.ad>tbody>tr>td.plc{background:white !important;}
.pi{border-bottom:1px solid #CDCDCD !important;}
td.plc>.po.hin{border-top:none !important;}

/*-- 标题 --*/
.ts{font-size:15px !important;}
.plc.ptm.pbn.vwthd{background:#EEE !important;border-bottom:2px solid #2a77c8 !important;}
    
/*-- 色彩搭配--*/
.num >a ,.fold,#atarget.y ,a.y.xi2.op{color:#424242 !important;}
a:hover{color:#007cd8 !important;}
#forumnewshow td>a{
border-color:#DEF2FC!important;
background-color:#F7FCFF !important;
color:#39AFEA !important;
}
.newthread >tr >*{
background-color:#F7FCFF  !important;
}
* [color="#FFA200"] ,.showmenu >font{color:#2A77C8!important;}
.ptn.xg2 *[color="#FF0000"]{color:#007cd8 !important;}
em.xw0.xi1 ,.xi1 ,.authi>*[color="red"]{color:#2A77C8 !important;}
.xw1 >* >span.xi1{background-color:#def2fc !important;}
h3{color:#007cd8 !important;background:#F9F9F9 !important;}
.icon_ring.vm{border-color:#007cd8 !important;margin-top:-3px!important;}
/*下一页*/
.pgbtn >.bm_h,.mn >#autopbn.bm_h{border:none !important;padding:0px !important;border-radius:0;}

/*-- 精简界面 --*/
/*[[Sign]]*/
/*[[UserInfo]]*/
.authi >img ,.btn_s_close >.vm,#separatorline {display:none !important;}
a.fastre,a.editp{background-image:none !important;}
.chart.z{background-image:none!important;margin-left:-18px;color:#656565;}
/* === 其他页面修复 === */
/*输入框*/
input{background-image:none!important;}
input ,.tedt ,#postbox >.edt{border:1px solid #CDCDCD !important;}
.y.o{border:none !important;}
/*回复框美化*/
#fastpostsubmit,#postsubmit ,#attach_confirms ,button.pn ,.y.o >a{
    font-weight:bold !important;
    color:#222 !important;
    background:url('http://a.ikafan.com/image/common/pn.png?LYD') repeat-x scroll 0px 0px #E5E5E5 !important;
    border:1px solid #CDCDCD;
    box-shadow:none !important;
}
#typeid_fast_ctrl,#typeid_ctrl,#e_fontname,#e_fontsize{
    border:1px solid #CDCDCD;
    background-color:white !important;
    background:url('http://a.ikafan.com/image/common/arw_r.gif?LYD');
    background-repeat: no-repeat;
    background-position: 95% 50%;
}
#extra_additional_b,#extra_additional_c{
    margin-left:1px !important;
    border:1px solid #FEFEFE !important;
    background:#FEFEFE !important;
    box-shadow:none !important;
}
#postbox >.pbt.cl{background:white;padding:5px !important;border:1px solid #CDCDCD ;}

/*设置、收藏、好友等界面修复*/
#nv_home >#wp >#ct{background:white!important; margin-top:15px !important;}
#nv_home >#hd{background:white !important; height:45px !important;}

#nv_portal >#hd >.wp >.hdc >#um > p:nth-child(3){display:none !important;}
#nv_portal {margin-top:-60px !important;}
/*版区信息*/
.bm.bml.pbn{padding-bottom:0 !important;}
.bm.bml.pbn >.bm_h.cl >.xs2 ,.bm.bml.pbn >.bm_h.cl >.y ,.bm.bml.pbn >.bm_h.cl >.o{margin-top:-3px !important;}
.bm.bml.pbn >.bm_c.cl.pbn{padding:2px 10px !important;}/*版主*/
/*淘帖订阅按钮美化*/
.clct_flw{ 
    margin-top:-1px !important;
    border:1px solid #BBB ;}
.clct_flw >#followlink >.u  ,.clct_flw >#followlink >i{
    color:#424242 !important;
    border-right:1px solid #BBB;
    background:#DBDBDB !important;
}
.clct_flw >#follownum_display{
    padding:0 8px;
    color:#424242 !important;
    background-image:none !important;
}
/*资讯专区边框改进*/
.bm.bml.pbn >.bm{
border-left:none !important;
border-right:none !important;
}
.bm.bml.pbn >.bm.bw0{
padding:2px !important;
}
/* 未登录模块自动隐藏 */
.fastlg_fm{display:none !important;}
#lsform  {
position:absolute !important;
width: 380px !important;
right:0 !important;
top: -2px !important;
padding:0 20px 10px 0!important;
background:white !important;
z-index:55 !important;
opacity: 0.1 !important;
}
#lsform:hover{
opacity: 1 !important;
} 
    
}
@-moz-document domain("fs.kafan.cn") {
    body{background:white !important;}
    .scform_srchtxt{border:none !important;}
}
