/*point.im dark-grey*/

/*@namespace url(http://www.w3.org/1999/xhtml);*/
@-moz-document domain("point.im"){
    input, textarea {-moz-appearance:none !important;}
    input::-webkit-outer-spin-button, input::-webkit-inner-spin-button, input::-o-inner-spin-button, input::-o-outer-spin-button, input[type="file"]::-webkit-file-upload-button {margin:0; -webkit-appearance:none;}
    x::-webkit-file-upload-button, input[type="file"]::after {margin:0; -webkit-appearance:button;}

    :focus {-moz-outline:none; outline:none;}
    button::-moz-focus-inner, input[type="reset"]::-moz-focus-inner, input[type="button"]::-moz-focus-inner, input[type="submit"]::-moz-focus-inner, input[type="file"] > input[type="button"]::-moz-focus-inner {border:none;}
    button::focus-inner, input[type="reset"]::focus-inner, input[type="button"]::focus-inner, input[type="submit"]::focus-inner, input[type="file"] > input[type="button"]::focus-inner {border:none;}

    /* custom scrollbar fix*/
    html, body { width: 100% !important; }
    body { margin: 0 10px !important; overflow-x: hidden; }
    body > div { margin-left: -10px !important; }
    #footer { margin-left: 0 !important; }
    /* End of
    custom scrollbar fix*/
    
    html, button, input, select, textarea {
    	color:#acacac !important;
    }
    body {
      font-size: 0.9em !important;
    }
    html, body, #header-wrap, #main-wrap, #new-post-form,
    #tree-switch a.active,
    #user-menu a:hover,
    #user-menu a.active,
    #order-switch a.active {
    	background-color:#303030 !important;
    }
    a,
    .button,
    input[type="button"],
    input[type="submit"],
    input[type="file"]::after,
    input[type="file"] > input[type="button"],
    .link,
    .post .reply-label,
    .post .more-label,
    .post .post-id a .cn,
    .aside #counters #subs .value,
    .aside #counters #readers .value,
    #new-post-hide-label,
    #content .users > .user .info .user {
    	color:#00aef0 !important;
    }
    code {
    	color:#2FCE6E !important;
    }
    pre {
    	color:#929292 !important;
    }
    .button,
    input[type="button"],
    input[type="submit"],
    input[type="text"],
    input[type="password"],
    input[type="email"],
    input[type="date"],
    input[type="datetime"],
    textarea,
    input[type="file"],
    input[type="file"]::after,
    input[type="file"] > input[type="button"],
    #header #search-form,
    #header #search-form input[type="text"],
    #subheader-wrap,
    .tag,
    #main #left-menu a.active,
    #tree-switch a,
    code,
    .codehilite,
    pre,
    #user-menu,
    .aside-toggle,
    #content .users > .user,
    .tag-info > h1,
    #left-menu .unread,
    #user-menu .unread,
    .post .post-id a .unread,
    #comments .post.unread,
    #header .search-form,
    #order-switch a,
    #scrollToTop {
        background-color:#373737 !important;
    }
    #content .codehilite {
        background:#373737 !important;
    }
    .post .post-id a .unread,
    #left-menu .unread,
	#left-menu .active .unread,
	#user-menu .unread {
		color: #FF5100 !important;
	}
    input[type="text"],
    input[type="password"],
    input[type="email"],
    input[type="date"],
    input[type="datetime"],
    textarea,
    .button,
    input[type="button"],
    input[type="submit"],
    input[type="file"],
    #order-switch a.active,
    #scrollToTop {
		border:1px solid #404040 !important;
		box-shadow:none !important;
    }
    #search-form input[type="text"],
    #search-form input[type="submit"],
    input[type="file"]::after,
    input[type="file"] > input[type="button"] {
    	border:none !important;
    }
    input[type="file"]::after,
    input[type="file"] > input[type="button"] {
    	border-right:1px solid #404040 !important;
    }
    #main-wrap, .post, #content h1, #user-menu, #sections-menu, #content .users > .user {
        border-color:#404040 !important;
    }
    #header-wrap {
        border-bottom-color:#303030 !important;
    }
    #subheader-wrap {
        border-bottom:none !important;
        box-shadow:0 0 20px rgba(0, 0, 0, 0.35) !important;
    }
    #subheader > .active::before, #subheader > .active::after {
        border-color:rgba(48, 48, 48, 0) rgba(48, 48, 48, 0) #303030 !important;
    }
    #header #user-menu-label .avatar {
        border-color:transparent !important;
    }
    .tag::before, .tag-info > h1:before {
        border-color:rgba(55, 55, 55, 0) #373737 !important;
    }
    #footer #menu > div a, .aside #counters .value {
        text-shadow:none !important;
    }
    #main #left-menu #top-link {border-color:#373737 rgba(55, 55, 55, 0) !important;}
    #main #left-menu #top-link:focus {-moz-outline:none !important; outline:none !important;}
    #subheader > .active {color:#808080 !important;}
    .post .info .created, .aside #counters .units {
        font-family:"Droid Sans","PT Sans","Open Sans",Verdana,sans-serif !important;
    }
    #new-post-hide-label, .link {border-bottom-color:#00aef0 !important;}
    .post .text blockquote {border-left-color:#404040 !important;}
    .footnote {border-top-color:#404040 !important;}
    .aside-toggle::after {border-color:rgba(255,255,255,.25) transparent !important;}
    .codehilite {background: none !important;}
    .saved {background-color: #34D038 !important; color: #fff !important; border: none !important;}
    .errors {background-color: #CE0000 !important; color: #FFF8DD !important; border: none !important;}
    
