    #scrollToTop {
        border-radius: 0 !important;
        bottom: 10px !important;
        right: 5px !important;
        font-size: 12px !important;
        font-weight: 400 !important;
        padding: 4px !important;
        width: auto !important;
    }
    .totopshow {opacity: 0 !important;}
    .totopshow:hover {opacity: 1 !important;}
    #scrollToTop:focus {-moz-outline: none !important; outline: none !important;}

    input[type="file"]::-webkit-file-upload-button, x::-webkit-file-upload-button {
    	background-color:#373737 !important;
    	color:#00aef0 !important;
    	border:none !important;
    	border-right:1px solid #404040 !important;
    }

    ::-webkit-scrollbar {
        width: 9px !important;
        height: 9px !important;
    }
    ::-webkit-scrollbar-track {
        -webkit-border-radius: 0 !important;
        border-radius: 0 !important;
        background-color: #303030 !important;
    }
    ::-webkit-scrollbar-track:vertical {
        width: 9px !important;
    }
    ::-webkit-scrollbar-track:horizontal {
        height: 9px !important;
    }
    ::-webkit-scrollbar-thumb {
        -webkit-border-radius: 0 !important;
        border-radius: 0 !important;
        background-color: #00aef0 !important;
    }
    ::-webkit-scrollbar-thumb:vertical {
        min-height: 50px !important;
        width: 9px !important;
        background: -webkit-linear-gradient(left, rgba(0,174,240,0) 0%, rgba(0,174,240,0) 1%, rgba(0,174,240,0) 24%, rgba(0,174,240,1) 25%, rgba(0,174,240,1) 75%, rgba(0,174,240,0) 76%, rgba(0,174,240,0) 100%) !important;
    }
    ::-webkit-scrollbar-thumb:horizontal {
        min-width: 50px !important;
        height: 9px !important;
        background: -webkit-linear-gradient(top, rgba(0,174,240,0) 0%, rgba(0,174,240,0) 24%, rgba(0,174,240,1) 25%, rgba(0,174,240,1) 75%, rgba(0,174,240,0) 76%, rgba(0,174,240,0) 100%) !important;
    }
    ::-webkit-scrollbar-thumb:hover, ::-webkit-scrollbar-thumb:active {
        background: #00aef0 !important;
    }
    ::-webkit-resizer, ::-webkit-scrollbar-corner {
        background-color: #303030 !important;
        background: -webkit-linear-gradient(-45deg, rgba(48,48,48,0) 0%,rgba(48,48,48,0) 49%,rgba(48,48,48,1) 50%,rgba(48,48,48,1) 100%) !important; /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(48,48,48,0) 0%,rgba(48,48,48,0) 49%,rgba(48,48,48,1) 50%,rgba(48,48,48,1) 100%) !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    }

/* AGENT_SHEET */
/*@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);*/
    scrollbar {
        -moz-appearance:none!important;
        background: #303030 !important;
        border-radius: 0 !important;
    }
    scrollbar[orient="vertical"] {
        width: 9px !important;
    }
    scrollbar[orient="horizontal"] {
        height: 9px !important;
    }
    scrollbar thumb {
        -moz-appearance: none !important;
        background-color: transparent !important;
        border: none !important;
        border-radius: 0 !important;
    }
    scrollbar thumb[orient="vertical"] {
        min-height: 50px !important;
        width: 9px !important;
        background: -moz-linear-gradient(left, rgba(0,174,240,0) 0%, rgba(0,174,240,0) 1%, rgba(0,174,240,0) 24%, rgba(0,174,240,1) 25%, rgba(0,174,240,1) 75%, rgba(0,174,240,0) 76%, rgba(0,174,240,0) 100%) !important;
    }
    scrollbar thumb[orient="horizontal"] {
        min-width: 50px !important;
        height: 9px !important;
        background: -moz-linear-gradient(top, rgba(0,174,240,0) 0%, rgba(0,174,240,0) 24%, rgba(0,174,240,1) 25%, rgba(0,174,240,1) 75%, rgba(0,174,240,0) 76%, rgba(0,174,240,0) 100%) !important;
    }
    scrollbar thumb[orient="vertical"]:hover,
    scrollbar thumb[orient="vertical"]:active,
    scrollbar thumb[orient="horizontal"]:hover,
    scrollbar thumb[orient="horizontal"]:active {
        background: #00aef0 !important;
    }
}
