css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitch.tv") {
 
    .ember-view.ember-text-area, #chat_text_login.text
    {
        background-color: #020202 !important;
    }
    
    .button-simple.primary, .button.primary_button
    {
        border: none !important;
        background: #001658 !important;
        background-color: #001658 !important;
        color: #bbbbbb !important;
    }
    #chat_speak.primary_button
    {
        background: none !important;
        background-color: #001658 !important;
        color: #bbbbbb !important;
    }
    
    .ember-view.ember-text-area, #chat_text_login.text
    {
        color: #ececec !important;
    }
    
    DIV.chat-interface, .ember-view.ember-text-area, #controls, #chat_text_login.text
    {
        box-shadow: 1px 1px 1px #1b1b1b !important;
        border-style: solid !important;
        border: none !important;
        border-radius:1px !important;
    }
    
    .ember-text-area:focus, #controls, #chat_text_login.text
    {
        box-shadow: 1px 1px 2px #002673 !important;
        border-style: solid !important;
        border-color: #001658 !important;
        border: 1px !important;
    }

    BUTTON.viewers.button-simple.light.tooltip, .settings.button-simple.light.tooltip
    {
       background-color: #000 !important;
    }

    .rightcol-content.js-rightcol-content, DIV.js-chat-hidden-overlay.chat-hidden-overlay, DIV.scroll.chat-messages.js-chat-messages.hideTimestamps.hideModIcons,
    DIV.rightcol-content.js-rightcol-content, .ember-view.ember-chat,  #right_nav.segmented_tabs
    {
        background-color: #000 !important;
    }
    
     .chat-interface, #controls
    {
        background-color: #1b1b1b !important;
    }
    
    SPAN.message, .chat_line
    {
     color: #ececec !important;
    }
    
    .tab, .selected.tab
    {
        text-shadow: none !important;
        background: none !important;
        color: #bbbbbb !important;
        box-shadow: none !important;
        border-style: none !important;
        border-color: #4c4c4c !important;
        border-radius: 0px !important;
        background-color: #1b1b1b !important;
    }
    
    span.from:not([style="color:#D2691E"]):not([style="color:#0000FF"]):not([style="color:#FF7F50"]):not([style="color:#F61A8C"]):not([style="color:#5F9EA0"]):not([style="color:#00FF7F"]):not([style="color:inherit"]):not([style="color:#2E8B57"]):not([style="color:#FFFFFF"]):not([style="color:#B22222"]):not([style="color:#8A2BE2"]):not([style="color:#FF4500"]):not([style="color:#9ACD32"]):not([style="color:#FF31FF"]):not([style="color:#FF0000"]):not([style="color:#DAA520"]):not([style="color:#1E90FF"]):not([style="color:#FF69B4"]):not([style="color:#008000"])
    {
    	color:#00CCCC!important;
    }

    #chat_settings_dropmenu_button.dropdown_glyph, #chat_viewers_dropmenu_button.dropdown_glyph
    {
    color: #FFF!important;
     background-color: #000 !important;
     border: none !important;
     border-radius: 0px !important;
     box-shadow: none !important;
    }
    
    .settings.button.normal_button, .viewers.button.normal_button
    {
     color: #FFF!important;
     background-color: #000 !important;
     border: none !important;
     border-radius: 0px !important;
     box-shadow: none !important;
    }
    
    .ember-chat .chat-messages .chat-line.notification .message
    {
        color: red !important;
        font: 28px/1.5 "Helvetica Neue",Helvetica,Arial,sans-serif;
    }
    
    
    .ember-view.ember-list-view
    {
        background-color: #000 !important;
    }
    
    .chat-menu-header 
    {
        color: #ececec !important;
        background-color: #1b1b1b !important;
    }
    
    .drag_handle.chat-top-header, .chat-menu-button-container.left, #popout.chat-menu-button-container.right
    {
         background-color: #1b1b1b !important;
    }
    
    .ember-list-item-view span a
    {
        color: #1E90FF !important;
    }
    
    .chat-menu-content
    {
         background-color: #1b1b1b !important;
    }
     
    .chat-menu .chat-menu-content p
    {
        color: #ececec !important;
    }
    
    .list-video.item.clearfix, .list-header, p.no-content, .room
    {
        background-color: #000 !important;
        border-bottom: 0px !important;
    }
    .chat-menu-button-container.left, .chat-header.main-header
    {
        background:#1b1b1b !important;
        border-bottom: 0px !important;
        border-right: 0px !important;
        box-shadow: none !important;
    }
    
    .chat-room
    {
    background-color: #000 !important;
    border-bottom: 0px !important;
    border-top: 0px !important;
    top: 36px !important;
    }
    
    .chat-header.all-rooms-header
    {
    background-color: #000 !important;
    border-bottom: 0px !important;
    border-top: 0px !important;
    border-right: 0px !important;
    box-shadow: none !important;
    }
    
    P.room-title
    {
    color: #bbbbbb !important;
    }
    
    .js-rooms.scroll.chat-rooms>.tse-scroll-content
    {
     background: #000000 !important;
    }
    
}
