css:
@-moz-document domain("tracker.tekeremata.org") {


.nfo {
  font-family:terminal;
  font-size:10px;
}
hr {
  border:1px dotted #0099FF;
  height:1px;
  margin-left:auto;
  margin-right:auto;
  width:60%;
}
p {
  margin:0;
  padding:0;
}
ul, ol {
  list-style-position:inside;
  list-style-type:none;
}
a, img {
  border:0 none;
  outline:none;
}
h1 {
  color:#666666;
  font-size:11pt;
}
h2 {
  color:#444444;
  font-size:10pt;
}
h3 {
  color:#444444;
  font-size:9pt;
}
h4, h5, h6 {
  font-size:8pt;
}
body {
  background-color:#ccc;
  font-family: 'MS Sans Serif', Geneva, sans-serif !important;
  font-size:9pt;
  margin:0;
}
.wrapper {
  display:block;
  margin:0;
  padding:0;
  position:relative;
  width:100%;
}
.header {
  display:block;
  height:91px;
  margin:0;
  padding:0;
  position:relative;
  width:100%;
}
.logo {
  content:url(http://tekeremata.org/wp-content/uploads/2012/07/logoteke.png) !important;
  display:block;
  float:none !important;
  height:80px !important;
  margin:0 !important;
  padding:0;
  position:relative;
  top:10px;
  width:410px !important;
}
.infobar {
  clear:right;
  color:#666666;
  display:block;
  float:right;
  margin:0;
  padding:0;
  position:absolute;
  right:60px;
  top:20px;
  z-index:2;
}
#main {
  display:block;
  margin:0;
  padding:6px;
  position:relative;
}
.credits {
  background-color: #ccc;
  display:block;
  line-height:16px;
  margin:0;
  padding:10px;
  text-align:center;
  width:auto;
}
input, select {
  border:1px solid #777777;
  font-family:Arial, Helvetica, sans-serif;
  margin:1px;
  padding:2px;
}
input {
  font-size:11px;
}
select {
  font-size:10px;
}
textarea {
  -webkit-box-shadow:#CCCCCC 0 2px 4px 1px inset;
  background-color:#EEEEEE;
  border:1px solid #AAAAAA;
  box-shadow:#CCCCCC 0 2px 4px 1px inset;
  padding:7px;
}
input[type="reset"], input[type="button"], input[type="reset"], input[type="submit"] {
  -webkit-box-shadow:#555555 0 0 2px 1px;
  background-image:-webkit-linear-gradient(top, #CFCFCF 0%, #A5A5A5 100%);
  background-position:initial initial;
  background-repeat:initial initial;
  box-shadow:#555555 0 0 2px 1px;
  cursor:pointer;
  font-family:Arial, Helvetica, sans-serif;
  font-size:12px;
  font-weight:bold;
  margin:2px;
  padding:1px 2px 1px 4px;
}
input[type="reset"]:hover, input[type="button"]:hover, input[type="reset"]:hover, input[type="submit"]:hover {
  color:#FFFFFF;
  text-shadow:#000000 0 1px 2px;
}
input[type="reset"], input[type="button"], input[type="reset"] {
}
input[type="reset"]:hover, input[type="button"]:hover, input[type="reset"]:hover {
}
input[type="submit"] {
}
input[type="submit"]:hover {
}
input[type="text"] {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  border:1px solid #444444;
  box-shadow:#FFFFFF 0 0 0 1px inset;
  font-family:Arial, Helvetica, sans-serif;
  font-size:13px;
}
input[type="password"] {
  color:#FF6600;
}
a:link, a:visited, a:active {
  color:#006699;
  text-decoration:none;
}
a:hover {
  color:#003366;
  text-decoration:underline;
}
.navigation {
  background-image:url(http://tracker.tekeremata.org/themes/default/images/menu.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
  display:block;
  height:27px;
  margin:0;
  padding:0;
  text-align:center;
  width:100%;
}
.menu {
  clear:both;
  display:table;
  height:27px;
  margin-left:auto;
  margin-right:auto;
  margin-top:0;
  padding:0;
  width:auto;
}
.menu ul {
  display:table-row;
  margin:0;
  padding:0;
}
.menu ul li {
  display:table-cell;
  margin:0;
  padding:0;
}
.menu ul li a {
  color:#FFFFFF;
  display:block;
  font-size:12px;
  font-weight:bold;
  height:27px;
  line-height:27px;
  margin:0;
  padding:0 18px;
  text-decoration:none;
  text-shadow:#000000 0 1px 1px;
  width:auto;
}
.menu ul li a:hover {
  color:#111111;
  text-shadow:none;
}
.bMenu {
  display:block;
  margin:0;
  padding:0;
  width:100%;
}
.bMenu ul {
  display:block;
  list-style-position:inside;
  list-style-type:circle;
  margin:0;
  padding:0;
}
.bMenu ul li {
  color:#666666;
  text-decoration:none;
  width:auto;
}
#navigate ul {
  border-bottom-color:#AAAAAA;
  border-bottom-style:solid;
  border-bottom-width:1px;
  list-style-type:none;
  margin:0 auto;
  padding:0;
  width:98%;
}
#navigate ul li {
  border-collapse:collapse;
  border-left-color:#AAAAAA;
  border-left-style:solid;
  border-left-width:1px;
  border-right-color:#AAAAAA;
  border-right-style:solid;
  border-right-width:1px;
  border-top-color:#AAAAAA;
  border-top-style:solid;
  border-top-width:1px;
  margin:0;
  padding:0;
  width:100%;
}
#navigate ul li a {
  background-image:-webkit-linear-gradient(top, #EEEEEE 0%, #DDDDDD 100%);
  background-position:initial initial;
  background-repeat:initial initial;
  color:#555555;
  display:block;
  font-family:Arial, Helvetica, sans-serif;
  font-size:12px;
  font-weight:bold;
  height:18px;
  line-height:18px;
  margin:0;
  padding:0;
  text-align:left;
  text-indent:5px;
  width:100%;
}
#navigate ul li a:hover {
  color:#0066CC;
  text-decoration:none;
  text-indent:8px;
}

/* Marquee container */

#marqueecontainer{
	position: relative;
	/*width: 200px; marquee width */
	height: 200px; /*marquee height */
	background-color: #ccc !important;
	overflow: hidden;
	/*border: 3px solid orange;*/
	padding: 2px;
	padding-left: 4px;
	}



#nMember, #uOnline {
  font-family:Verdana, sans-serif;
  margin:2px;
  padding:2px;
  width:98%;
}
#nMember p, #uOnline p {
  display:inline;
}
#nMember ul, #nMember li, #uOnline ul, #uOnline li {
  color:#0066CC;
  display:inline;
  margin:0;
  padding:0;
}
.myBlock {
  display:block;
  margin:0;
  padding:0;
  width:100%;
}
.myBlock-caption {
  background: #c3c3c3;
  background-position:50% 0;
  border-top:2px solid #FFFFFF;
  color:#FFFFFF;
  display:block;
  font-family: 'MS Sans Serif', Geneva, sans-serif !important;
  font-size:15px;
  font-weight:bold;
  height:25px;
  line-height:25px;
  margin:0;
  padding:0;
  text-align:center;
  text-shadow:#000000 0 1px 2px;
}
.myBlock-content {
  border-bottom-color:#777777;
  border-bottom-style:solid;
  border-bottom-width:1px;
  border-top-color:#777777;
  border-top-style:solid;
  border-top-width:1px;
  display:block;
  padding:4px;
}
.myBlock-foot {
  display:hide !important;
}
.myFrame {
  border-top:2px solid #4C4C4C;
  -webkit-box-shadow: 0 5px 5px #777;
  -moz-box-shadow: 0 5px 5px #777;
  display:block;
  margin:0;
  padding:0;
  width:100%;
}
.myFrame-caption {
  background-color:#DE9000;
  background-position:50% 0;
  color:#FFFFFF;
  display:block;
  font-family: 'MS Sans Serif', Geneva, sans-serif !important;
  font-size:15px;
  font-weight:bold;
  height:25px;
  line-height:25px;
  margin:0;
  padding:0;
  text-align:center;
  text-shadow:#000000 0 1px 2px;
}
.myFrame-content {
  border-bottom-color:#777777;
  border-bottom-style:solid;
  border-bottom-width:1px;
  border-top-color:#777777;
  border-top-style:solid;
  border-top-width:1px;
  display:block;
  padding:4px;
}
.myFrame-foot {

  display:hide !important;

}
.showthecross {
  float:left;
  margin-left:2px;
  margin-right:0;
  margin-top:0;
}
.ttable_headouter {
  border-collapse:collapse;
}
.ttable_headinner {
  border:1px solid #777777;
  border-collapse:collapse;
}
.ttable_head {
  background-color:#BFDBFF;
  background-position:50% 0;
  border:1px solid #777777;
  color:#000000;
  font-family:Verdana;
  font-size:8pt;
  line-height:20px;
  text-align:center;
}
.ttable_col1 {
  background-color:#E2E2E2;
  border:1px solid #777777;
  font-family:Verdana;
  font-size:8pt;
}
.ttable_col2 {
  background-color:#CECECE;
  background-position:50% 0;
  border:1px solid #777777;
  font-family:Verdana;
  font-size:8pt;
}
.ttable_colx {
  background-color:#CECECE;
  background-position:50% 0;
  border:1px solid #777777;
  font-family:Verdana;
  font-size:8pt;
}
.table_table {
  border:1px #777777;
  border-collapse:collapse;
}
.table_head {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  background-color:#BFDBFF;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/p-title.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
  border:1px solid #777777;
  box-shadow:#FFFFFF 0 0 0 1px inset;
  color:#000000;
  font-family:Verdana;
  font-size:8pt;
  line-height:20px;
  text-align:center;
}
.table_col1 {
  background-color:#E2E2E2;
  border:1px solid #777777;
  font-family:Verdana;
  font-size:8pt;
}
.table_col2 {
  background-color:#CCCCCC;
  background-position:50% 0;
  border:1px solid #777777;
  font-family:Verdana;
  font-size:8pt;
}
#downloadbox {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  background-image:-webkit-linear-gradient(top, #83B8FF 0%, #3D91FF 100%);
  background-position:initial initial;
  background-repeat:initial initial;
  border:1px solid #949494;
  box-shadow:#FFFFFF 0 0 0 1px inset;
  padding:10px;
}
#downloadbox a {
  color:#FFFFFF;
  font-size:13px;
  font-weight:bold;
}
.download {
  border:2px solid #777777;
  padding:10px;
}
.download legend {
  -webkit-box-shadow:#333333 0 0 4px;
  background-image:-webkit-linear-gradient(top, #CFCFCF 0%, #A5A5A5 100%);
  background-position:initial initial;
  background-repeat:initial initial;

  box-shadow:#333333 0 0 4px;
  color:#FFFFFF;
  font-size:12px;
  margin:0;
  padding:3px 13px;
}
.shoutbox_body {
  background-color:white;
  color:black;
  font-family:verdana;
}
.shoutbox_contain {
  border:0;
  overflow:auto;
  width:100%;
}
.shoutbox_history {
  border:0;
  height:100%;
  width:100%;
}
.shoutbox_messageboxback {
  background-color:white;
}
.shoutbox_noalt {
  background-color:#E5E5E5;
}
.shoutbox_alt {
  background-color:#EEEEEE;
}
.shoutbox_msgbox {
  background-color:#E5E5E5;
  border:1px solid #777777;
  font-size:12px;
  width:500px;
}
.shoutbox_shoutbtn {
  background-color:#F0F8FF;
  border:1px solid black;
  font-size:12px;
  font-weight:bold;
}
.shoutbox_error {
  color:red;
  font-size:13px;
}
.error {
  color:#FF0000;
}
.f-border {
  border:1px solid #666666;
  border-collapse:collapse;
}
.f-header {
  background-color:#2E9EE2;
  background-position:50% 0;
  border:1px solid #608EAB;
  display:block;
  font-size:11px;
  margin:0;
  padding:0;
  width:100%;
}
.f-logo {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/f-logo.gif);
  background-position:50% 0;
  background-repeat:no-repeat no-repeat;
  box-shadow:#FFFFFF 0 0 0 1px inset;
  display:table;
  margin:0;
  padding:0;
  width:100%;
}
.f-logo td {
  height:28px;
  padding:6px;
}
.f-logo td a {
  color:#DDDDDD;
  font-weight:bold;
  text-decoration:none;
  text-shadow:#000000 0 1px 2px;
}
.f-logo td a:hover {
  color:#FFFFFF;
  text-shadow:#000000 0 1px 1px;
}
.f-logo td img {
  vertical-align:middle;
}
.f-location {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/f-location.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
  border:1px solid #949494;
  box-shadow:#FFFFFF 0 0 0 1px inset;
  color:#FFFFFF;
  display:block;
  font-family:Arial, Helvetica, sans-serif;
  font-size:11px;
  font-weight:bold;
  height:28px;
  line-height:28px;
  margin-bottom:8px;
  text-align:left;
  text-indent:10px;
  text-shadow:#000000 0 1px 2px;
}
.f-location a {
  color:#FFFFFF;
  font-family:Verdana, Arial, Helvetica, sans-serif;
  font-size:12px;
  text-shadow:none;
}
.f-cat td {
  -webkit-box-shadow:#FFFFFF 0 0 0 1px inset;
  box-shadow:#FFFFFF 0 0 0 1px inset;
}
.f-title {
  background-color:#9DC7FF;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/f-title.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
  color:#FFFFFF;
  font-weight:normal;
  height:24px;
  line-height:24px;
  padding:0;
  text-shadow:#000000 0 1px 1px;
}
.f-title a {
  color:#FFFFFF;
  text-decoration:none;
  text-shadow:#000000 0 1px 1px;
}
.p-title {
  background-color:#9DC7FF;
  background-position:50% 0;
  color:#FFFFFF;
  font-family:Verdana, Arial, Helvetica, sans-serif;
  font-weight:bold;
  height:26px;
  padding:3px;
  text-shadow:#000000 0 1px 1px;
}
.p-title a {
  text-shadow:none;
}
.p-foot {
  background-position:50% 0;
  border-collapse:collapse;
  color:#333333;
  font-size:10px;
  font-weight:normal;
}
.f-reply {
  border:1px solid #777777;
  color:#333333;
  font-size:10px;
  font-weight:normal;
  padding:3px;
}
.f-cat {
  background-color:#EE9A00;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/f-cat.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
  color:#FFFFFF;
  font-family:Verdana, Arial, Helvetica, sans-serif;
  font-size:13pt;
  font-weight:bold;
  height:27px;
  margin:0;
  padding:0;
  text-shadow:#000000 0 1px 1px;
}
.f-img {
  background-color:#EEEEEE;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/alt1.png);
  background-position:0 0;
  background-repeat:no-repeat no-repeat;
  padding-left:18px;
}
.f-row:hover .f-img {
  background-color:#D5D5D5;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/alt2.png);
}
.f row td.atl1, .f row td.alt3 {
}
.f-row:hover .alt1, .f-row:hover .alt3 {
  background-color:#D5D5D5;
  background-image:none;
  background-repeat:no-repeat no-repeat;
}
.alt1:hover, .alt3:hover {
}
.alt2 {
  background-color:#CDCDCD;
  background-image:url(http://tracker.tekeremata.org/themes/default/images/alt3.gif);
  background-position:50% 0;
  background-repeat:repeat no-repeat;
}
.small {
  font-family:Verdana, Arial, Helvetica, sans-serif;
  font-size:10px;
}
.comment-details, .bb-btn {
  background-color:#DDDDDD;
}
.comment, .bb-comment {
  background-color:#EEEEEE;
}

}
