css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("justthetalk.com")
{
/*
Colours:
#DEFFFF = rgb(222,255,255)
#330000 = rgb(51,0,0)

#D5D2CD = rgb(213,210,205)
#42341c = rgb(66,52,28)

#EBEBEB = rgb(235, 235, 235)
*/


/* 2 columns / hide logo row BEGIN 
	#logorow { display: none; }    Use this to hide the logo row
	#discussionsidebar { display: none; }
	#folders { display: none; }
 2 columns only END */
 
 INPUT#email{width: 300px;}

* { font-family : verdana, "Bitstream Vera Sans", "DejaVu Sans", helvetica, tahoma, geneva, arial, sans-serif;}
	code,kbd,pre,samp{
		font-family:Menlo,Monaco,Consolas,"Courier New",monospace
	}
.bordered {
    border-top: 1px transparent;
    padding-top: 6px;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #DEFFFF;
    border: 1px solid #D5D2CD;
    border-radius: 4px 4px 4px 4px;
    box-shadow: 3px 3px 3px 0px rgba(100, 100, 100, 0.2);
}

*, *:before, *:after {
    -moz-box-sizing: border-box;
}
body {

    font-family: "Helvetica Neue",verdana,Helvetica,Arial,sans-serif;
    font-size: 12px;
    line-height: 0.08;
    color: rgb(51, 51, 51);
	background-color: #D5D2CD;
}
	
   .btn.btn-primary {background-color: #EBEBEB;padding: 5px 10px;font-size: 12px;line-height: 1.5; border-radius: 3px 3px 3px 3px;  color: #330000}
   .btn.btn-primary:hover, .btn.btn-primary:focus { background-color: #DEFFFF; padding: 5px 10px;font-size: 12px;line-height: 1.5; border-radius: 3px 3px 3px 3px; color: #42341c; }

   #donatebutton.btn.btn-sm.btn-default,.btn-primary {background-color: #EBEBEB;  padding: 5px 10px;font-size: 12px;line-height: 1.5; border-radius: 3px 3px 3px 3px;  color: #330000}
   #donatebutton.btn.btn-sm.btn-default:hover,#donatebutton.btn.btn-sm.btn-default:focus,.btn-primary:hover, .btn-primary:focus { background-color: #DEFFFF; padding: 5px 10px;font-size: 12px;line-height: 1.5; border-radius: 3px 3px 3px 3px; color: #42341c; }

   /* Spoiler, thread header */
	.panel-body,.panel-heading,.threadouterheader {
		color: rgb(51, 51, 51);
		background-color: #EBEBEB;
		border-color: rgb(221, 221, 221);
		font: 11px verdana, arial, helvetica, sans-serif;
		color: #42341c;
		padding: 3px 3px;
		border-bottom: 1px  transparent;
		border-top-right-radius: 1px;
		border-top-left-radius: 1px;
	}   
	.panel {
		margin-bottom: 20px;
		background-color: #EBEBEB;
		border: 1px transparent;
		border-radius: 4px 4px 4px 4px;
		/*box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);*/
	}   
   
   .btn-sm { padding: 5px 10px;font-size: 12px;line-height: 1.5; border-radius: 3px 3px 3px 3px;}
   
	.btn-default {
		color: rgb(51, 51, 51)!important;
		background-color: #EBEBEB!important;
		border-color: rgb(204, 204, 204)!important;
	}
	
	.postheader {
		width: 100%;
		border-top: 1px transparent;
		padding-top: 4px;
		vertical-align: top;
	}
	#postlist,.threadouterheader{
		padding: 10px 10px;
		margin-bottom: 10px;
		margin-top: 10px;
		width: 100%;
		margin-bottom: 20px;
		background-color:#DEFFFF!important;
		border-radius: 4px 4px 4px 4px;
		border: 1px solid #EBEBEB;/*rgb(227, 227, 227);*/
		
	}
	.posttitle,.bordered.threadnav {
		margin-bottom: 5px;
		margin-top: 5px;
		width: 100%;
		color: #330000!important;
		background-color:#DEFFFF!important;
		border-color: rgb(204, 204, 204)!important;
		font: 11px verdana, arial, helvetica, sans-serif;
	}
	.postbody{
		border-bottom: 1px solid #EBEBEB;/*rgb(227, 227, 227);*/	
	}

	a{
		color:#42341c;
		text-decoration:none;
		background:transparent
	}
	
    a:link,a:visited{
		outline:0;
		color:#42341c;
		background-color:#DEFFFF;
	}

	a:active,a:hover,a:focus{
		outline:1;
		color:#42341c;
		background-color:#DEFFFF;
		text-decoration:underline;
	}

	a[href]:after{
		/*	content:" (" attr(href) ")";*/
		content:"";
		color:#42341c;
		background-color:#DEFFFF;
	}

	abbr[title]:after{
		content:" (" attr(title) ")";
		color:#42341c;
		background-color:#DEFFFF;
	}

	a[href^="javascript:"]:after,a[href^="#"]:after{
		content:"";
		color:#42341c;
		background-color:#DEFFFF;
	}

.alert-info, .post-ignored, .alert-warning, .post-moderated-deletedbyuser, .post-moderated-watch, .post-moderated-suspended, .post-moderated-deleted, .post-moderated, .post-ignored {
	clear: both;
	background-color: #D5D2CD; /* #DEFFFF; */
	padding: 4px;
	margin-bottom: 12px;
    font: 10px verdana, arial, helvetica, sans-serif;
	line-height: 1.5em;
	color:#330000;
	margin-left: auto;
	margin-right: auto;
}

  #threadtop{color: #330000!important; background-color:  #DEFFFF!important;}

    p { color: #42341c; }
    .discussionlistitem > div { color: #330000; font-size: 100% !important;}



    .alert-success { display: none; }

	#topbar,ol.breadcrumb{background-color:#DEFFFF;margin-top: 8px;margin-left: 8px;margin-bottom: 8px;margin-right: 8px;}
	
    #header,#bannerdesc,#toolbar,div.row,#logorow ,#headerText{ color:#DEFFFF;background-color:#DEFFFF; margin: 0;	padding: 0px;margin-bottom: 2px; background-color: rgb(247, 247, 247);box-shadow: 3px 3px 3px 0px rgba(100, 100, 100, 0.2);}

    .alert-info, .alert-warning {border: 0}

    .post-ignored { border-top: 1px transparent #ccc; margin: 2px}

    .post-quoted { font-size: 90% !important; }
	#bannerdesc{color:#DEFFFF}

    code { color: #333; background-color: #ddd; }

    #folders { line-height: 1.7em }
	
	#content {
		color:#42341c;
		background-color:#DEFFFF
	}

    #rightcontent { line-height: 1.7em }

	.pagination-sm>li>a,.pagination-sm>li>span{
		padding:5px 10px;
		font-size:8px
	}

	.pagination-sm>li:first-child>a,.pagination-sm>li:first-child>span{
		border-bottom-left-radius:3px;
		border-top-left-radius:3px
	}

	/* Bar that shows the folder picker, logout buttons */
	.breadcrumb,.headerTextLink > span:nth-child(1){
		padding:8px 15px;
		margin-top:20px;
		margin-bottom:20px;
		list-style:none;
    color: #42341c;
	background-color: #EBEBEB;
		border-radius:4px;
	}

	/*Page number boxes */
	.pagination>li>a,.pagination>li>span{
		position:relative;
		float:left;
		padding:6px 12px;
		margin-left:-1px;
		line-height:1.428571429;
		text-decoration:none;
    color: #42341c;
	background-color: #DEFFFF;
		border:1px solid #EBEBEB;/*rgb(227, 227, 227);*/
	}

	div.row,.postbody > p {
		margin-bottom: 0px;
		background-color: #DEFFFF;
		padding: 4px;
		clear: both;
		margin-bottom: 12px;
		   font: 10px verdana, arial, helvetica, sans-serif;
		line-height: 1.5em;
		margin-left: auto;
		margin-right: auto;
	}
	
	.posttextarea{
		display: block; 
		vertical-align: top;
		width: 100%;
		height: 180px;
		font: 12px verdana, arial, helvetica, sans-serif;
		line-height: 1.5em;
		margin-left: auto;
		margin-right: auto;
	}

	.biotext{
		display: block; 
		vertical-align: top;
		width: 400px;
		height: 180px;
	}

	.nobullets{
		list-style: none;
		margin-left: 0px;
		padding-left: 0px;
	}

	.threadouterheader{
		width: 100%;
		margin-top: 14px;
		margin-bottom: 14px;
	}

	.threadheader,.threadheading{
		border-top: solid #c0c0c0 1px;
		background-color: #EBEBEB;
		padding: 4px;
		margin-bottom: 12px;
	}

	.threadheadingdesc {
		margin-top: 12px;
		font-weight: bold!important;
		margin-bottom: 12px;
		color: #42341c;
		font: 13px verdana, arial, helvetica, sans-serif!important;
	}

	.threadheadingdiscussionheader {
		font-weight: bold;
		margin-bottom: 12px;
		color: #42341c;
		font: 12.5px verdana, arial, helvetica, sans-serif;
	}

	.headertext{
		padding-left:4px;
		font: 12px verdana, arial, helvetica, sans-serif;
		margin-bottom: 12px;
		color: #42341c
		
	}
	
	.threadlocked{
		font-style: italic;
		color: #42341c;
		margin-bottom: 12px;
	}

	.threadnav{
		margin-bottom: 12px;
		width: 100%;
		clear: both;
			font: 14px verdana, arial, helvetica, sans-serif;
		color: #42341c;
	}

	
/* sm-3 */
	.col-sm-3 {
		background-color: #DEFFFF;
		font: 11px verdana, arial, helvetica, sans-serif;
		color: #42341c;
	}
	
/* sm-6 */
	.col-sm-6 {
		background-color: #DEFFFF;
		font: 11px verdana, arial, helvetica, sans-serif;
		color: #42341c;
	}
	
	
}
