css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.frazpc.pl") {
    
#content{
background: none !important;}
   
    .col-b,.col-c,.col-d,.col-e{
        background-color:#1E1E1E!important;
    color:gray!important;}
    
    .solved{
        color:yellow!important}
    
    tr>.col-b>a,.readed>.col-b>a,.readed>.col-d,.readed>.col-e{
        color:gray!important;}
    .readed>.col-c{
        color:gray!important;}
    .readed>.col-c>a{
        color:orange!important}
    
    .col-c>a{
        color:gray!important}

    .my_topic{
color:springgreen!important;}
    
body{
background:#1E1E1E!important;}
    
form.search{
right: -58px !important;}

nav ul{
position: relative !important;
right: -173px !important;}

.register{position: relative;
    width: 47%!important}
    
    #content section{
margin: auto !important;
float: none !important;
padding: 5px !important;}

#content-wrapper-b, #content-wrapper-a, #content-wrapper{
background:none !important;}
    
header, .back.arrow, .infiniteCarousel, .change-skin, .linki_top, #empty, #empty-bottom, #content-second, #bottom-wrapper, #footer-wrapper, .fb-like-box, .satyr{
display: none !important;}

.author,.topic-author{
color:orange!important;}

.topic-info-a>a,p>a, blockquote>a{
color:#0080C0!important}
    
h1{
color:silver!important}
        
.secondary-one,.secondary-one>h3{
background: -moz-linear-gradient(top,#484848,#111111)!important;}
   
    .caption,caption,.h2-wrapper{
background: -moz-linear-gradient(top,#484848,#111111) !important;
color:silver!important;} 


.login-a, u{
color:#0080C0!important}

.board-links{
display: none !important}
.register{
display: inline !important}
    
#formModeratePost-reason,#formAddTopic-title, #formAddTopic-post, #formAddPost-post, 
#formAddPost-title,#formAddPost-title-element{
background: #4E4E4E!important;
        border:none !important;
color:silver!important}
    
    #formEditPost,#formEditPost-title,#formEditPost-post{
background: #4E4E4E!important;
        border:none !important;
color:silver!important}
    
#formEditPost,#formAddTopic,#formAddPost,.form_adds{
    background:#404040 !important;
    border:none !important;}
        
label, strong, .users-stats {
color:silver!important}

    select{
background:#4E4E4E!important;
color:grey!important;
    border:none !important;}
    
    a>strong, .adult{
        color: orange!important}

    BLOCKQUOTE{
color:silver!important}

.comment.old_post{
        background: #4E4E4E!important;}
    
    .board_activities>p>a{
        color:yellow!important}
    .edit_time,.edit_time>a{
color:yellow!important}
    
    #content aside{
display: none !important;}
#file_uploadUploader
    {
        width: 81px!important;
        height: 22px!important
    }
}
