}


#posts .post .post_content {
  background: transparent !important;
}


#posts .post {
  background: rgba(255,255,255,.3) !important;
  border-radius: 10px !important;
}




.tracked_tags {
background-color: rgba(255,255,255,0)  !important;
border-radius: 0px !important;
}



.popover_menu_item {
  background: rgba(255,255,255,1) !important;
  border-bottom: 1px solid #d97880 !important;
  
  
}



.note_item {
    background-color: #fbe7e8 !important;
    border-radius: 15px !important;
    border: hidden !important;
}

.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after {border-left-color: #d97880 !important}

.post_full.is_note .answerer .name, .post_full.is_note .asker .name, .post-composer_note-post .speaker .name {color: #d97880!important}
        
