/*source:*/.post .post_source_name_prefix {color: #d97880!important}

.post.fan_mail {
    color: #d97880 !important;
}

.quote {
    color: #d97880 !important;
}


.link_text_container{
    background-color: #fff !important;
}
  
  
 


.plexi.color.active.show {
  background-color: rgba(255,255,255,.4) !important;
}
.drop_target {
  background-color: rgba(255,255,255,.5) !important;
}
.drop_zone {
  border: solid 2px rgba(217,120,128,1) !important;
  border-radius: 2px !important;
}
