.photo_field.drop_zone_container .drop_zone .url_button:hover {
  color: rgba(207,147,152,1) !important;
}
#tumblelog_select > .txt:hover:after,
.post_options_wrapper .cog:hover,
.chrome.blue.options {
  opacity: 1 !important;
}
#tumblelog_select > .txt:after,
.post_options_wrapper .cog {
  opacity: 1 !important;
}


/*settings icon sopra upload pic panel*/.cog {background: transparent url('http://i.imgur.com/W2vmfBa.png') -40px 0 no-repeat}




#posts .post_content blockquote{border-left: solid 2px #d97880!important}


pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small{background:rgba(251,231,232,.7)!important;color:rgba(217,120,128,1)!important;font:bold .9em "arial",consolas,courier!important;white-space:pre-wrap!important;white-space:-moz-pre-wrap!important;white-space:-pre-wrap!important;white-space:-o-pre-wrap!important;word-wrap:break-word!important}




.elevator{color:rgba(0,0,0,0)!important;background: url('http://i.imgur.com/RxKpHg8.png')!important;width:50px!important;height:50px !important;position:absolute!important;margin-top:-620px!important;margin-left: -45px !important;}

.chat_line:nth-child(even),PRE{background-color:rgba(255,255,255,0)!important;
color:rgba(207,147,152,1)!important}


.answer_container,
.notes LI{background:rgba(255,255,255,1)!important;
}




li.selected, li.recessed, a.active, div.post_filter_tabs a {

