css:
@-moz-document domain('facebook.com')  {

@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 400;
  src: local('Montserrat-Regular'), url(http://themes.googleusercontent.com/static/fonts/montserrat/v4/zhcz-_WihjSQC0oHJ9TCYL3hpw3pgy2gAi-Ip7WPMi0.woff) format('woff');
}

* {
 font-family: 'Montserrat', sans-serif !important; 
}

#blueBar, .fbNubFlyoutHeader, .fbNubFlyoutBody, .fbNubFlyoutFooter, .fbNubFlyoutAttachments {
background-color: #fff !important;
border-color: #ebeef4;
border-bottom: 1px #ebeef4;
}


._585- {
-webkit-box-shadow: inset 0 0 0 1px #526da4, inset 0 1px 0 0 #526da4;
}

.hasSmurfbar, #blueBarHolder, #blueBar {
background-image: none !important;
}

#pageLogo a {
background-color: #FFF;
background: url("http://i.imgur.com/YLRIIkW.png") no-repeat center !important;
background-size:18px 18px;
margin-top:-2px;
margin-left:-1px;
}

#pageLogo a:hover {
background: url("http://i.imgur.com/pMilBDD.png") no-repeat center !important;
}

#pageLogo {
margin-left: -6px;
z-index: 5;
}

/* part of this next bit hides some of the facebook info in the right bar. 
   am i allowed to do that? is that illegal? sorry :-( */
.fbTimelineSideAds,
#rightCol #pagelet_ego_pane .ego_column:nth-child(1),
.fbPhotoSnowboxAds,
.fbPhotoSnowboxAdsSide,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"],
a[href*="/campaign/111111"], 
.fbEmuMarketplace,
#egoRefreshAds,
#pagelet_ego_pane_m,
#pagelet_ego_pane_w,
.fbAdUnit,
#pagelet_rhc_footer,
.rhcFooterWrap,
.profile_sidebar_ads, .sidebar_ads, .adcolumn_header, .admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads, #pagelet_bookmark_nav,
.headerTinymanName
{
background:none !important;
display:none !important
}

#fbDockChat,
#rightCol {
display: block !important;
}

}
