css:
/*
 * papermodelplane theme for Slack
 * www.papermodelplane.com
 * twitter.com/developer
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slack.com") {
  #team_menu, #col_channels, .channels_list_holder h2,
  #current_user_name, #active_channel_name,
  .light_theme .message, .light_theme .message .timestamp,
  #menu, .menu_example, #emoji_menu,
  .light_theme h1, .light_theme h2, .light_theme h3, .light_theme h4,
  #channel_members_toggle, ul.member_list li, .activity_item, .file_name,
  #message-input, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  }

  #team_menu {
    background: #6e7880;
    border: 0;
    font-size: 1.4rem;
    font-weight: 300;
  }

  #team_menu i {
    color: #fff; 
  }
  
  #col_channels {
    background: #e8edee;
    color: #6e7880;
  }

  .channels_list_holder h2 {
    color: #b0b8bb; 
  }

  .channels_list_holder h2.hoverable:hover {
    color: #9aa0a3;
  }

  .channels_list_holder ul li {
    height: 1.75rem;
    line-height: 1.75rem;
  }

  .channels_list_holder ul li a {
    height: 1.75rem;
    border-radius: 0;
  }

  .channels_list_holder ul li a:hover {
    background: #b0b8bb;
    color: #fff;
  }

  #channel-list, .channels_list_holder ul li a {
    color: #6e7880;
  }

  .channels_list_holder ul li.unread a,
  .channels_list_holder ul li.unread .prefix {
    color: #ff5c4e;
  }

  .channels_list_holder ul li.member .im_close,
  .channels_list_holder ul li.group .group_close {
    top: 7px;
  }

  #col_channels ul li.channel.active a.channel_name,
  #col_channels ul li.group.active a.group_name,
  #col_channels ul li.member.active a.im_name,
  .channels_list_holder ul li.channel.active a.channel_name .prefix,
  .channels_list_holder ul li.group.active a.group_name .prefix {
    background: #ff5c4e;
    color: #fff;
  }

  .list_more {
    font-size: 0.7rem;
    color: #b0b8bb !important;
    border-bottom: 0;
  }

  .list_more:hover {
    color: #9aa0a3 !important;
    border: 0;
  }

  #user_menu {
    background: #6e7880;
    border-color: #6e7880;
    color: #fff;
  }

  #user_menu i {
    color: #fff;
  }

  #connection_status {
    font-size: 0.8rem;
  }

  #help_icon, #flex_menu, #flex_toggle {
    border: 0;
  }

  .light_theme .message .meta {
    font-size: 0.8rem;
  }
}
