css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.arcgames.com/en/games/star-trek-online/news"), url("https://www.arcgames.com/en/games/star-trek-online/news") {
    
body {
    background-image: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/framework/main-background.jpg") !important;
    background-color: #000 !important;
}

header.header {
    height: 32px !important;
}
nav.module-tabs-platform { /*Platform tab*/
    display: none;
}    
div.news-list-banner { /*carosel*/
    display: none;
}    
div.news-content3 { /*videos*/
    display: none !important;
}
div.news-content { /*normal news items*/
    transform: none !important;
    width: 600px !important;
    height: 210px !important;
    position: relative !important;
    margin: 0px 300px !important;
    background: rgba(0, 0, 0, 0.6) none repeat scroll 0 0 !important;
    border-color: rgba(255, 255, 255, 1) !important;
    border-left-color: rgba(255,255,255, 0) !important;
    border-right-color: rgba(255,255,255, 0) !important;
    
}
div.news-content:hover { 
    width: 604px !important;
    height: 212px !important;
    padding-top: 14px !important;
    padding-left: 14px !important;
    padding-right: 10px !important;
    border-left-color: rgba(255,255,255, 1) !important;
    border-right-color: rgba(255,255,255, 1) !important;
    border-width: 1px !important;
}
    
div.news-content:hover > p.posting-infos { 
    margin-right: 4px !important;
}
div.news-content:hover > a.read-more  { 
    margin-right: 14px !important;
}
    
    
div.news-content > a {
    display:inline-block !important;
    margin-right:10px !important;
    width: auto !important;
    float: left !important;
}
div.news-content > a.read-more {
    float: right !important;
}
div.news-content > h2.news-title {
    width: 299px !important; /* one pixel more breaks formatting despite adding up to container width exactly */
    vertical-align:top !important;
    position: relative;
}
div.news-content > p.posting-infos {
    width: 299px !important; /* one pixel more breaks formatting despite adding up to container width exactly */
    vertical-align:top !important;
    display:block !important;
    float: right !important;
}

div.news-content > c {
    width: 299px !important; /* one pixel more breaks formatting despite adding up to container width exactly */
    vertical-align:top !important;
    display:block !important;
    float: right !important;
}
div.news-content > div.news-social {
    width: 100% !important; /* one pixel more breaks formatting despite adding up to container width exactly */
    vertical-align:top !important;
    display:block !important;
    clear:right !important;
	margin-top: 0px !important;
}
div.news-content > div.news-social > p > span {
    width: 40% !important;
}
    
    
    

h1.game-name {
    background: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/framework/bg-lnkHome.png") no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important; /*important for Chrome*/
    height: 140px !important;
    text-indent: -9999px;
    background-position: center !important; 
}
div.module-menus-content {
    width: 950px !important;
}
nav.module-menus-wrap {
    width: 950px !important;
    margin: 0px 145px !important;
}
li.languages {
    margin: 0px !important;
    top: 3px;
}
li.welcome {
    margin: 0px 10px !important;
    height: 25px !important;
    top: 5px;
    padding: 0px !important;
}
li.shopping-cart {
    margin: 5px !important;
    top: 90px !important;
}
div.user-bar-inner > ul {
    margin: 0px 0 0 !important;
}
li.arc-download { /*Arc download links*/
    display: none !important;
}
div.nav-bar-inner {
    width: 700px !important;
    padding: 0px;
    margin: 0px auto !important;
    margin-top: 25px !important;
}
ul.dropdown-content > li {
    line-height: 30px !important;
    margin-left: 0px !important;
    margin-right: 50px;
}
ul.dropdown-content > li > a {
    color: #888 !important;
}
ul.dropdown-content > li > a:hover {
    color: #fff !important;
}
a.logo {
    margin-top: 0px !important;
    height: 30px !important;
    width: 80px !important;
    background-position: 0 -200px !important;
}

}
