css:
@-moz-document domain("online.asb.co.nz") {
table.masterlayout td.content table#layout {
    max-width: 100%;
}
table.fncGrid tbody td {
    padding: 2px 5px;
    height: 20px;
}
table.fncGrid td.tableTitle {
    padding-top: 1px;
    padding-bottom: 2px;
}
a:link {
    text-decoration: none;
    color: black;
}
a:visited {
    text-decoration: none;
    color: black;
}
a:hover {
    text-decoration: underline;
    color: black;
}
table#layout td#Banner {
    visibility: hidden;
    display: none;
}
table.fncGrid thead th {
    color: #FFB800;
}
table.fncGrid thead tr td {
    color: #919191;
    font-style: italic;
}
table.fncGrid thead tr td b {
    font-weight: normal;
    font-size: 80%;
}
table thead tr.columnTitles th {
    background-color: #2A2A2A;
}
.CurrentPage {
    background-color: #FFB800;
}
}
