css:
@-moz-document regexp(".*/wiki/.*"), regexp(".*/w/index.php.*") {
body.mediawiki.skin-vector {
  font-size: 20px;
  max-width: 1100px;
  margin: 0 auto;
  position: relative;
  background: #f6f6f6 linear-gradient(to bottom, white, white 60px, #f6f6f6 100px) no-repeat;
  padding-right: 2.5em;
}
body.mediawiki.skin-vector div#content {
  border-right-width: 1px;
}

body.mediawiki.skin-vector #mw-head {
  margin: 0 1.5em;
  width: calc(100% - 1.5em);
}

body.mediawiki.skin-vector #mw-page-base {
  background: none;
}
}
