css:
@-moz-document domain(imgur.com) {

html, body, .side-copyright, #fixed-side-footer-links {
	background-color: #000;
}
.panel, .textbox, .main-image .image, #side-gallery-controls, #side-gallery-controls .selection, .pane {
	-webkit-border-radius: 0;
	-moz-border-radius: 0;
	border-radius: 0;
	background-color: #111 !important;
	border-top: none;
}
#side-gallery .nano>.pane>.slider {
    background:#222 !important;
}
#topbar {
	background-color: #000;
	border-bottom: none;
}
#secondary-nav, .user-nav {
	border-right: none;
	border-left: none;
}
.navPrev, .navNext, .next-prev, .navBrowse, .navTop, .header .sort-options a, .header .sort-options ul li {
	/*border-radius: 0 !important;*/
	border: 0;
}
.main-image h2.album-title {
	font-family: 'Roboto', sans-serif;
	font-size: 28px;
	margin: 0 0 10px !important;
	color: #85BF25;
	line-height:100%;
	font-weight:300;
}
.textbox {
	font-family: 'Roboto', sans-serif;
	padding: 10px 0;
	color: #888 !important;
	font-size: 14px;
	line-height: 150%;
}
.comment {
	font-family: 'Roboto', sans-serif;
	border-bottom: 1px solid #222;
	padding-bottom: 5px;
}
.next-prev {
	margin-right: 9px;
}
.navPrev, .navBrowse, .navTop {
	background: #222 !important;
}
.navNext {
	background: #477200 !important;
}
.content, .nano.has-scrollbar, .slider {
	border-radius: 0 !important;
}
#side-gallery .nano {
	background: #111 !important;
}
.next-prev-browse.panel.fixed {
	top: 10px !important;
}
#caption_textarea {
	border: 0;
	background: #222;
	border-radius: 4px 0 0 4px;
	padding: 0 8px
}
.submit-caption-button {
	background: #477200 !important;
	border-radius: 0 4px 4px 0;
	margin-left: 0;
	padding: 1px 2px;
	border: 0
}
h2#image-title {
	font-family: 'Roboto', sans-serif;
	font-size: 17px;
	font-weight:300;
}
.report-icon, .meme-icon, .social, .icons, .favorite-icon {
	background-color: #111;
}
.caption-wrapper {
	padding-top: 10px;
}
.main-image #under-image .info {
	padding-top: 7px;
}
.post a:link img {
	border-color: #111;
}
.usertext.textbox.caption {
	font-size: 13px;
	line-height: 120%;
	padding-top: 0;
	padding-left: 5px;
	margin-bottom: 15px;
}
.header.textbox.bold.small-margin-bottom .inline {
	padding-top: 2px;
	line-height: 100%;
}
.search.placeholder {
	background: #222;
	border: 0;
	padding: 5px 7px;
	font-size: 13px;
	line-height: 150%
}
h2.header {
	background:#111 !important;
	border-radius: 0 !important;
  	border:0 !important;
}
.account-arrow {
    display:none;
}
.comments-info .inline {
	line-height: 100%;
}
#side-gallery .nano .scroll-content * {
	font-family:'Roboto', sans-serif;
	font-weight:300;
}
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #000;
}
::-webkit-scrollbar-thumb {
    background: #477200;
    -webkit-border-radius: 0;
}
::-webkit-scrollbar-corner {
    background: #000;
}
}
