css:
@-moz-document url-prefix("http://hench.wikia.com/") {
@font-face {
  font-family:"PeachMilk";
  src:url("http://img1.wikia.nocookie.net/__cb20140306183359/hench/images/4/43/Peach_milk_eot.jpg?") format("eot"),
    url("http://img3.wikia.nocookie.net/__cb20140306182953/hench/images/e/ee/Peach_milk_ttf.jpg") format("truetype");
  font-weight:normal;
  font-style:normal;
}

body {
    background: #bb0000; /* Old browsers */
    background: -moz-linear-gradient(top,  #bb0000 1%, #000000 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#bb0000), color-stop(100%,#000000)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #bb0000 1%,#000000 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #bb0000 1%,#000000 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #bb0000 1%,#000000 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #bb0000 1%,#000000 100%); /* W3C */
}

#WikiaPage {
  margin-top: 8px;
/* Shadow syntax: [x-offset] [y-offset] [blur amount] [spread] */
-webkit-box-shadow: 8px 8px 0px 0px rgba(200, 0, 0, 1),
                    16px 16px 2px 0px rgba(0, 0, 0, 0.8),
    				-8px 8px 0px 0px rgba(200, 0, 0, 1),
                    -16px 16px 2px 0px rgba(0, 0, 0, 0.8);
-moz-box-shadow:    8px 8px 0px 0px rgba(200, 0, 0, 1),
                    16px 16px 2px 0px rgba(0, 0, 0, 0.8),
    				-8px 8px 0px 0px rgba(200, 0, 0, 1),
                    -16px 16px 2px 0px rgba(0, 0, 0, 0.8);
box-shadow:         8px 8px 0px 0px rgba(200, 0, 0, 1),
                    16px 16px 2px 0px rgba(0, 0, 0, 0.8),
    				-8px 8px 0px 0px rgba(200, 0, 0, 1),
                    -16px 16px 2px 0px rgba(0, 0, 0, 0.8);
}


/**************/
/* NAVIGATION */
/**************/
.WikiNav * {
  font-weight: bold !important;
  font-variant: small-caps !important;
  font-size: 1.01em !important;
}
.WikiNav .navbackground {
  border-bottom-left-radius: 3px;
}
.WikiHeader > nav .subnav {
  background-color: #D00;
  color: #FFF;
}
.subnav-2a:hover {
  text-decoration: none !important;
}
.WikiHeader > nav .subnav-2 li.marked2 .subnav-2a {
  background-color: #D00;
  border: 1px solid transparent;
  color: #FFF;
}
.WikiHeader > nav .subnav-2 li.marked2 .subnav-2a:hover {
  background-color: #D00;
  color: #FFF;
  border: 1px solid transparent;
}
.WikiHeader > nav .subnav-2 li.marked2 .subnav-2a > .chevron, .WikiHeader > nav .subnav-2 li .subnav-2a:hover > .chevron {
  border-top-color: #FFF;
}
.subnav-2 .subnav-3, .subnav-2:hover .subnav-3 {
  border: none !important;
  top: 30px !important;
  padding: 0 !important;
}
.subnav-2 .subnav-3a {
  background-color: #D00;
  color: #FFF !important;
  font-weight: bold;
}
.subnav-3a:hover {
  background-color: #000 !important;
  color: #FFF !important;
}


/****************/
/* PAGE HEADERS */
/****************/
.WikiaPageHeader {
  border-bottom: solid 4px #D00 !important;
  box-shadow: 0 6px 5px -6px #000;
}
h1 {
  font-family: PeachMilk;
  font-size: 3.25em !important;
  color: #000;
}
#toc h2 {
  border-bottom: none !important;
  font-size: 1.5em !important;
}
.WikiaArticle h2 {
  font-family: PeachMilk;
  font-size: 2em !important;
  color: #1b1b1b;
  border-bottom: solid 2px #1b1b1b !important;
}

/*********************/
/* CHARACTER INFOBOX */
/*********************/
.wikia-infobox {
    margin-right: 5px;
	box-sizing: border-box;
    border: solid 5px #D00;
    box-shadow: 5px 5px 5px -4px #000, -5px 5px 5px -4px #000;
  	padding: 8px;
}
.wikia-infobox-header {
	background-color: #D00;
    text-align: center !important;
    padding: 8px 3px !important;
    font-variant: small-caps;
    color: #FFF;
  	border-top-left-radius: 8px;
  	border-top-right-radius: 8px;
}
.wikia-infobox-subtitle {
    top: -3px;
    text-align: center !important;
  	background-color: #000;
  	color: #FFF;
    padding: 4px !important;
  	border-bottom-left-radius: 8px;
  	border-bottom-right-radius: 8px;
}
.wikia-infobox-section-header {
    font-size: 1.15em;
    text-align: center;
}
}
