css:
@-moz-document domain("fanfou.com") {
/* 屏蔽个人认为多余的侧栏和底部和推荐 */
#goodapp, #reminder, #badge-sect, #trendtabs, #fanfou-ad-microwave-oven, #footer, #iconset, #sidebar .tags, .colltab + .sect, .sect:nth-child(2), #sidebar #user_rec { display: none !important; }
 
/* 去除 logo beta版字样 */
.global-header-content { background: transparent url(http://static2.fanfou.com/img/fanfou.png) 0 0 no-repeat !important; }

/* 还原背景为默认背景 */
body {
	background-color: #acdae5 !important;
	background-image: url(http://static.fanfou.com/img/bg/0.png) !important;
	background-repeat: no-repeat !important;
	background-attachment: fixed !important;
	background-position: top left !important; 
	color: #222222;
}

/* 侧栏栏换为白色 */
#sidebar {
    background-color: #FFFFFF !important;
    border-left: 1px solid #eeeeee !important;
    border-top-right-radius: 20px !important;
    border-bottom-right-radius: 20px !important;
}

/* 主内容边缘圆角 */
#main { 
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px; 
}

/* 侧栏去边框 */
#sidebar .sect { border-top: none; }   

/* 屏蔽设置中多余的选项 */
#settings .tabs li:nth-child(2), #settings .tabs li:nth-child(4), #settings .tabs li:nth-child(5), #settings .tabs li:nth-child(6), #settings .tabs li:nth-child(7), #settings .tabs li:nth-child(9) {display: none !important;}
#settings .tabs ul { text-align: left;padding-left: 10px; }  
#settings .tabs ul li { padding: 0 2px;}
.ui-roundedbox-content > ul > li:nth-child(4) {display:none;}


/* 在别人的 bio 前面加上任意内容 （自行修改）并换行。 \A 为换行编码，勿删。**/
/*#sidebar #bio span:before { white-space:pre; content:"任意内容~！\A"; } */
}
