@-moz-document url-prefix("http://www.n24.de/n24/Mediathek") {
body
    {
        background: #000 !important;
    }
div[class*='ads'], 
div[class*='ad-'], 
div#meta,
div#main_menu,
/*div#sub_menu,*/ 
div#breadcrumb,
div.footer_container,
div.tvbar,
div.container_12 div.grid_4,
div.mediathek_teaser_container,
div.e_023,
div.e_011,
