css:
@-moz-document domain("lifehacker.com"), domain("io9.com") {

#pagecontent,
.row {
       width: 100%;
       z-index: auto;
    }  

div.four
    {
       float: right;  
    }
    
.eight,
.row .eight 
    {
       width: 80%;
    } 

section.branch-wrapper div.post-wrapper
    {
       padding-top: 0px;
       padding-bottom: 0px;
    } 
    
.four,
.row .four 
    {
       width: 20%;
    }
    
nav.top-bar div.row div.twelve ul.top-bar-tools li.follow-buttons   
    {
        display: none
    }

nav.top-bar div.row div.twelve ul.top-bar-tools
    {
       float: right;
    }

.f_packages_on .top-bar.slim-toolbar.fixed .top-bar-tools 
    {
  width: auto;
    }

.f_packages_on .sidebar-module .sidebar-content 
    {
  top: 34px !important;
    }
    
 .f_packages_on .post-wrapper
    {
       padding-top: 10px;
       padding-bottom: 0px;
    }  
    
div.alert-box    
    {
        display: none
    }

#site-footer
.eight, 
.row .eight
    {
        width: 80%;
    }
    
.push-four
    {
        left: 0%;
    }
    
#site-footer   
.row 
    {
       z-index: auto;
       height: auto;
    } 

    
#container,
#container .mainContent,
#main-container .inner,
#main-container .inner .topchrome,
#main-container .gmgrid,
#comments,
.thread,
.replies,
div.comment { width: 100% !important; }

}
