css:
/***** Hide various worthless buttons *****/
.search-go-button {display:none !important}
#star-button {display:none !important}
#feed-button {display:none !important}
#searchbar .searchbar-dropmarker-image {display: none !important}



#searchbar .searchbar-engine-button {min-width: 22px !important; max-width: 22px !important;}

#searchbar .searchbar-engine-image {margin: -1px 0px !important;}



#notification-popup-box{
    
    margin-top: 3px !important;
    padding: 0px 2px !important;
}
    
#titlebar{
    margin: 0px 0px -15px 0px !important;
}

#urlbar .autocomplete-textbox-container {
  margin-top: -2px !important;
  margin-bottom: 0px !important;
  padding: 0px !important;
  height: 18px !important;
  max-height: 18px !important;
}

/**** The combined go/stop/reload button ****/
#urlbar > toolbarbutton {
    margin-top:4px !important;
}

#identity-box {
  margin: 1px 4px 1px 4px !important;
  padding: 0px !important;
  max-height: 19px !important;
}

#appmenu-button {
	background-image: url("chrome://branding/content/icon48.png") !important;
	background-repeat: no-repeat !important;
        font-size: 0px !important;
	background-position: 0px 1px !important;
	padding: 0px 3px 0px 0px !important;
	min-width: 0 !important;
	width: 60px !important;
        height: 14px!important;
	max-height: 14px !important;
}

.tabs-newtab-button {
   margin: 0px 0px 0px 0px!important;
   max-height: 21px !important;
}

.tab-icon-image, .tab-throbber {
   margin-top: -12px  !important;
   margin-bottom: -5px !important;
   max-width: 11px !important;
   max-height: 11px !important;
}

.tab-text {
   font-family: calibri !important;
   font-size: 10pt !important;
   margin-top: -9px !important;
}

.tab-close-button {
   margin-top: -2px !important;
   padding:8px 1px!important;
    
    
}

.tabbrowser-arrowscrollbox {
   margin-top: -8px !important;
   margin-bottom: -1px !important;
   max-height: 16px!important;
   margin-left: -4px;
}

#urlbar {
  border: 0 !important;
  margin: 0px 0px 4px 0px !important;
  font-size: 9pt !important;
  padding: 0px !important;
}

#nav-bar {
   margin: 0px 0px -4px 0px !important;
   padding: 0px !important;
   border: 0px !important;
   
}

.searchbar-textbox {
   border: 0px !important;
   margin: -0px 0px 4px 0px !important;
   padding: 0px !important;
   font-size: 9pt !important;
   max-height: 18px !important;
}

.toolbarbutton-icon {
  padding: 0px !important;
  margin: -11px 0px -6px 0px !important;
}
