css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.elotrolado.net") {

    /* Cambiamos letra por defecto a Verdana */
    
    body {
       font-family: verdana, sans-serif;
    }
    
    
    /* Añade pequeño borde al contorno del post para aumentar contraste*/
    
    .post.bg1 {
        border: 1px solid #BBB;
    }
    
    .post.bg2 {
        border: 1px solid #BBB;
    }
    
    /* Elimina linea naranja en tu post */
    .post.votedgood, .post:target, .post.votedgood:hover, .post:target:hover {
        border-left: 1px solid #BBB;
    }
    
    /* Añade borde para separar el avatar del post y usa un tono menos brillante para el background */
    .postbody {
        border-left: 1px solid #BBB;
        background-color: #E5F1E5;
    }
    
    span a:active {
        color: #395A2F;
        background-color: #E5F1E5;
        border-color: #395A2F;
    }
    
    /* Eliminamos color de fondo en los breadcrumbs */
    .breadcrumbs a:active {
        background: none;
    }
    
    /* Oscurece un poco el color de la fecha para aumentar el contraste */
    
    .postbody .date a.permalink {
        color: #888;
    }
    
    
    html, body {
        color: #585858;
    }
    
    /* Oscurece un poco el separador de la firma para aumentar el contraste */
    
    .signature {
        border-top-color: #BBB;
    }
    
    /* Oscurece un poco el texto de la firma para aumentar el contraste */
    .signature, .signature a:link {
        color: #585858;
    }
    
    .signature, .signature a:link, .signature a:visited {
        color: #585858;
    }
    
    /* Aumenta un poco el contraste en la vista general del foro */
    li.row {
        border-bottom-color: #BBB;
        padding-bottom: 1px;
    }
    
    
    .bg1 {
        background-color: #E5F1E5;
    }

    .bg2 {
        background-color: #E5F1E5;
    }
    
    li.row:hover {
        background: none repeat scroll 0% 0% #E5F1E5;
    }
    
    /* Aumentamos un poco el padding para mejor visibilidad en los foros generales */
    ul.forums li.row {
        padding-bottom: 5px;
    }
    
    /* Pero no en los posts de las wikis */
    div.wikis ul.topiclist.forums li.row {
        padding-bottom: 0px;
    }
    
    ul.forums {
        background: none repeat scroll 0% 0% #E5F1E5;
    }
    ul.forums:hover {
        background: none repeat scroll 0% 0% #E5F1E5;
    }
    .topicauthor a {
        color: #585858;
    }
    
    
    /* Elimina el blanco de los lados al disminuir el ancho */
    #wrap {
        background-color: #E6E6E6;
    }
    
    /* Aumenta contraste en los avisos */
    .notice {
        border-top-color: #BBB;
    }
    
    /* Aumentamos un poco la anchura maxima */
    #outer-content-wrap {
        min-width: 1004px;
        max-width: 1400px;

    }
    
    #header-wrap {
       min-width: 1004px;
       max-width: 1400px;
    }
    
    /* Triangulito postbody */
    .postbody:before{ 
        content:"";
        position:absolute;
        top:0;
        left:-18px;
        width:19px;
        height:18px;
        background:url(data:image/gif;base64,R0lGODlhEwASAKECALu7u+Xx5f///////yH+EUNyZWF0ZWQgd2l0aCBHSU1QACH5BAEKAAIALAAAAAATABIAAAIsRI6pKLbPGoSiupmqxVlLHngbJ45TaT5oqqxs5F6d+820HNKarHtX7zsAgwUAOw==) top center no-repeat;                     
    }
    
    /* Aumenta ligeramente el tamaño del texto de las encuestas */
    fieldset.polls dt {
        font-size: 13px;
    }
    
    /* Bordes y ligeras correcciones en botones de paginacion */
    .pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {
        font-family: verdana, sans-serif;
        border: solid 1px #BBB;
        
        background-color: #F2F2F2;
    }
    
    /* Negrita para botones de pagina */
    .topic-actions span a {
        /*font-weight: bold;*/
        
    }
    
    .pagination span strong {
        color: #FFF;
        background-color: #FF7F00;
    }
    
    /* Borde en botones de pagina */
    .pagination span strong {
        font-family: verdana, sans-serif;
        border: solid 1px #BBB;
    }
        
    /* Background gris para paginas de la lista de temas */
    .row .pagination span a, li.pagination span a {
        background-color: #F2F2F2;
    }
    
    /* Correccion de alineacion en egosearch */
    ul.linklist li {
        line-height: 27px;
    }
    
    /* Eliminar borde en los botones de la lista de topics */
    .rowbtn {
        border-right: none;
        /*background-color: #EEE;*/
    }
  
}
