css:
@-moz-document url-prefix("http://pinboard.in/add"), domain("pinboard.in") {
form {}
#instructions {
	color: #fff !important }
}

@-moz-document url-prefix("http://pinboard.in"), domain("pinboard.in") {
body {
	background:#fff url('http://dl.dropbox.com/u/1615956/grain.png');
	font: 76%/150% "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Arial, sans-serif;
	margin: 0;}
a, a:link {
	color: #3d332a !important;}
a:visited {
	color: #42423d !important}
a:hover {
            text-decoration: underline;}
h2, h3, .settings_heading, td.settings_heading {
            background: #e9e7e0;
            color: #a0998d;}
#content {margin-top:80px;
            clear: left;
            display: inline-block;
            height: 100%;
            padding:0px 15px;}
#content:after {
            	content: ".";
            	display: block;
            	clear: both;
            	visibility: hidden;
            	line-height: 0;
            	height: 0;}
html[xmlns] #content {
            	display: block;}
             
* html #content {
            	height: 1%;}        
#banner {position:fixed; top:0px;
                background: #444 !important;
                color: #fff;
                padding: 10px;
box-shadow:0px 2px 3px rgba(0,0,0,0.5);
-moz-box-shadow:0px 2px 3px rgba(0,0,0,0.5);
-webkit-box-shadow:0px 2px 3px rgba(0,0,0,0.5);
z-index:100;
border-bottom:1px solid #222 !important;
border-bottom-right-radius:10px;	-moz-border-radius-bottomright:10px;
border-bottom-left-radius:10px;	-moz-border-radius-bottomleft:10px;}

#logo {}
#logo .pin_logo {display: none;}
#logo #pinboard_name {background: url(data:image/png;base64,) center left no-repeat;
                        height: 14px;
                        line-height: 14px;
                        padding-left: 16px;}
#timer {color: #fff;}
#banner a, #banner a.banner_username{
                    color: #fff !important;}

#main_column { width: auto;}

#tag_cloud { float: right;}

a.bookmark_title {color:#094866 !important;}
a.bookmark_title.unread {color:#a11a1a !important}
.bookmark {border-left:4px solid rgba(0,0,0,0) !important;
border-bottom: 1px dashed #bbb !important;
                    padding: 10px 2px !important;
		margin-bottom:0px !important;}
a.bookmark_title {text-decoration:none !important;}
.bookmark:hover {background-color:#f5f5f5; border-left:4px solid rgba(20,60,120,0.5) !important;}
a.tag {border:1px solid #ccc; padding:2px 6px; background:#ddd;-moz-border-radius: 10px;
border-radius: 10px;}

a.tag:hover {text-decoration:none; background:#eee;}
.star {margin-left: 0;}

a.edit, a.delete, a.mark_read {padding:1px 4px; -moz-border-radius: 10px;
border-radius: 10px;border:1px solid #ddd;}
a.edit:hover, a.delete:hover, a.mark_read:hover {text-decoration:none;background-color:#fff;}
.display {float: right;
          width: 580px;}
.bookmark_title {font-size: 15px;}
.edit_links {margin-right:4px;
	opacity: 1;float: right;text-align: right;}
.edit_links a {color: #d1cfc9;}
.edit_link a:hover {font-weight: bold;}
.tabs {background: none;}
.tab {background: #e9e7e0;
      border-bottom: none;
      margin-right: 10px;}
.tab_selected {background: #fff;}
.tab_spacer {display: none;}
#tab_panes {border-top: 1px solid #aaa ;}
#right_bar {
                background: #f5f5f5;
                border:1px solid #bbb !important;
                border-bottom: 1px solid #aaa !important;
                float: right;
                padding: 10px;
		box-shadow:inset 0px 0px 20px rgba(100,100,100,0.1);
		-moz-box-shadow:inset 0px 0px 20px rgba(100,100,100,0.1);
		-webkit-box-shadow:inset 0px 0px 20px rgba(100,100,100,0.1);
		-moz-border-radius: 10px;
		border-radius: 10px;}
		
#right_bar a.tag {border:0px; padding:2px 6px; background:none;}
                #right_bar a:link, #right_bar a {
                    color: #777 !important;
                    font-weight: bold;}
#right_bar a.tag:hover {color:#555 !important;}
}
