css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.co.uk")

{
body {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#site-wrapper {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#wrapper {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#innerwrapper {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#topdeck {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#bottomdeck {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#content {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#future_company_footer {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#meta {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
#comments {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
ol.commentlist {background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;}
li.comment {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
blockquote.twitter-tweet {
background:#111111 none repeat scroll 0 0 !important;
color:#000000;
line-height:1;
}
div.comment-author.vcard {
background:#111111 none repeat scroll 0 0 !important;
color:#ffffff;
line-height:1;
}
div.title {
background:#111111 none repeat scroll 0 0 !important;
color:#ffffff;
line-height:1;
}
div.leaderboard {
background:#111111 none repeat scroll 0 0 !important;
color:#ffffff;
line-height:1;
}
#container {
margin:50px auto 0 !important;
}
#menubar{position: absolute !important; top: 0px !important; background: none !important; }
#logo-area{ width: 100% !important; height: 40px !important;  }
#splashPosts{width: 1050px !important;}
#logo-text{width: 535px !important;}
.post-permalink h1 a, .post h1 a, #content .post-full h1 a {
color:#ddd!important;
}
#sidebar #bestmodo {

display: none !important;
}
#sidebar {border-top:1px solid #CCCCCC !important;}
.spacer{
display: none !important;
}
p {color:#B0C4DE;} 
.excerpt{background:#111111 none repeat scroll 0 0 !important;}
.featureditem{background:#111111 none repeat scroll 0 0 !important;}
.mpu{background:#111111 none repeat scroll 0 0 !important;}
.postmeta{background:#111111 none repeat scroll 0 0 !important;}
p.must-log-in{background:#111111 none repeat scroll 0 0 !important;}
a {
color:#a7eeff!important
}
h1:visited,h2:visited,h3:visited,h4:visited,.post.top .content h1 a:visited,.classic_frontpage .post h2 a:visited,.bydate .post h2 a:visited,.post h1 a:visited {
color:#336680!important}
}
