css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("seattlepi.com") {
  div.hst-siteheader, div.span-10.last, div.hst-categorylist, 
  div.ck-region.ck-clearfix.ck-col-2.ck-region-pre-content, div#disqus_thread, 
  div.ck-block.ck-block-comic div.ck-body:last-child, #ck-adsense-23, 
  div.ck-block.ck-recommended-comics.ck-content, div.topmask div.hst-pagetitle.clearfix, 
  div.ck-region.ck-clearfix.ck-col-1.ck-region-header, div.hst-sitefooter.clearfix, div.ck-region.ck-clearfix.ck-col-2.ck-region-footer
  div.ck-region.ck-clearfix.ck-col-1.ck-region-footer, .middlemask,
  div.span-10, div.span-11c.last, div#BF_WIDGET_1, a.ck-toggle-comments, 
  div.ck-region.ck-clearfix.ck-col-2.ck-region-footer,
  div.ck-widget.ck-widget-overlay.ck-on, a.ck-nav-action.ck-zoom,
  div.ck-actions.ck-clearfix, div.ck-actions.ck-clearfix + a + div.ck-body {
    /*visibility: collapse !important;*/ display: none !important;
  }
  div.span-21b.clearfix, .topmask, div.ck-region.ck-clearfix.ck-col-3.ck-region-content,
  div.pagecontent.faux-21.clearfix, .span-21, .span-21b, .span-21c, div.span-21b.clearfix,
  div.container, div.faux-10.clearfix {
    padding: 0 !important; margin: 0 !important;}
  .ck-widget {
    background: #FFF !important; width: auto !important;
    padding: 0 !important; margin: 0 !important;}
  div.pagecontent.faux-21.clearfix, div.span-21b.clearfix, 
  div.ck-block.ck-block-comic, div.container {
    width: 800px !important;}
  div.container {
    position: fixed; top: 0; left: calc( 50% - 400px ); height: auto; width: 800px;}
  div#disqus_thread iframe#dsq-2 {display: none !important;}
}
