css:
@-moz-document domain("gocomics.com"){
  body {
    overflow-x: hidden;
  }
  #cookieconsent-container,
  .layout-2col-sidebar,
  .navbar,
  .item-interactions,
  .nav-tabs,
  .comic__buy-button,
  .js-comments-thread-container,
  .container h2,
  .gc-deck,
  #recommendations_holder,
  .amu-container-ad-wrapper,
  .amu-container-footer,
  .comic__interactions-container,
  .container .row:nth-child(2),
  .col-sm-6,
  .col-lg-4,
  .mb-4,
  .amu-container-alert,
  .gc-footer,
  .gc-site-header {
      display: none !important;
  }
  .item-comic-vertical,
  .comic.container {
      border: none !important;
  }
  .bg-white {
      background-color: transparent !important;
  }
  .amu-container-global {
      padding-top: 0 !important;
  }
  .content-section-padded {
      padding: 0 !important;
  }
  .gc-feature-header, .gc-page-container {
      margin: 0 !important;
  }
  .comic--can-buy {
      padding: 0 !important;
      margin: 0 auto !important;
  }
}
