css:
@-moz-document domain("www.spiegel.de") {
  body#home {
    background-image: none !important;
    background-color: maroon;;
  }
  #clicklayer, #spColumAd {
     display: none !important;
  }
#spHeader {
  position: absolute !important;
  top: 0;
  left: 0;
  margin-bottom: 3em !important;
}
#spContainer {
  position: absolute !important;
  top: 130px !important;
}
#spFooter {
  position: absolute !important;
  left: 800px !important;;
  top: 0px !important;;
  width: 150px !important;
   background-color: #efefef !important;
}
#spFooter #spTop {
  display: none !important;
}
#spFooter ul {
  margin: 2em;
}
#spFooter ul li {
  line-height: 1.4em;
}
#spMainContent {
   background-color: #efefef;
}
.spCommercial {
  display: none !important;
}
}
