css:
@-moz-document url-prefix("http://www.girlgeniusonline.com/comic.php") {
  body,
  td[width="700"][valign="middle"][align="center"] div[align="center"] p:first-child {
      margin: 0 !important;
  }
  table[width="759"][border="0"],
  table#Table_01,
  .fb_iframe_widget,
  hr,
  table#MainTable tbody tr[valign="top"][align="left"]:last-child,
  img,
  table[width="700"][height="92"],
  #SponsorTable,
  td[width="125"][align="left"][rowspan="3"] div[align="center"] center,
  td[width="125"][valign="top"][align="left"][rowspan="3"] {
      display: none !important;
  }
  img[alt*="Comic"] {
      display: block !important;
  }
  img[alt="Comic"] {
      margin-top: 20px;
      margin-bottom: 5px;
  }
  div[align="center"] {
      color: transparent !important;
      height: 28px;
  }
  img[alt="The First Comic"] {
      float: left;
      margin: 5px 0 5px 0;
  }
  img[alt="The Previous Comic"] {
      float: left;
      margin: 5px 0;
  }
  strong {
      color: #000;
      display: inline-block;
      height: 28px;
      line-height: 28px;
  }
  img[alt="The Next Comic"] {
      float: right;
      margin: 5px 0;
  }
  img[alt="The Most Recent Comic"] {
      float: right;
      margin: 5px 0 5px 0;
  }
  table#MainTable {
      width: 700px !important;
  }
  #MainTable tbody tr {
      display: none;
  }
  #MainTable tbody tr:nth-child(2),
  #MainTable tbody tr:nth-child(3),
  #MainTable tbody tr:nth-child(3) table tr {
      display: table-row;
  }
  /*#MainTable tbody tr:nth-child(3) table {
      position: relative; left: -73px;
  }*/
  #MainTable tbody tr:nth-child(3) td br {
      display: none;
  }
  #MainTable tbody tr:nth-child(3) td,
  #MainTable tbody tr:nth-child(3) td a {
      color: transparent !important;
  }
  #MainTable tbody tr:nth-child(3) div[align="center"] p {
      color: #000;
      margin-bottom: -16px !important;
  }
}
