css:
@-moz-document domain("bluemilkspecial.com"){
  html {
      height: 100% !important;
      width: 100% !important;
      padding: 0 !important;
      margin: 0 !important;
      overflow: hidden !important;
  }
  body {
      position: fixed !important;
      top: 0 !important;
      left: 0 !important;
      height: 100% !important;
      width: 100% !important;
      padding: 0 !important;
      margin: 0 !important;
      overflow: auto;
  }
  #sidebar-aboveheader,
  #sidebar-menubar,
  #sidebar-left,
  #comment-wrapper,
  #sidebar-right,
  div.post-content,
  #sidebar-footer,
  #footer,
  span.statcounter,
  script,
  noscript,
  iframe {
      display: none;
  }
  #page-wide-wrap {
      width: 1010px;
      margin: 0 auto;
  }
  #subcontent-wrapper {
      padding: 0 !important;
  }
  #content {
      visibility: hidden;
      margin: 0 !important;
      padding: 0 !important;
      border: none !important;
      width: 980px !important;
  }
  #content .nav {
      position: relative;
      top: 0;
      visibility: visible;
      float: none;
      margin: 0 !important;
      border-bottom: none !important;
  }
  div.post,
  p.footer-text {
      margin: 0 !important;
  }
}
