css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('karloskar.pingpong.se') {
    #header, #bread-crumbs, .box {
        border-radius: 0 !important;
    }
    #container-inner {
        max-width: 960px !important;
        min-width: 510px !important;
    }
    #header {
        background-color: white !important;
    }
    #main-header-top ul li a {
        color: black !important;
    }
    #Todos, #Events, #EntranceMessage, #MyMessages {
        display: none !important;
    }
    #logotype {
        height: 0 !important;
        width: 0 !important;
        /* these numbers match the new image's dimensions */
        padding-left: 322px !important;
        padding-top: 57px !important;
        margin-left: 15px !important;
        margin-top: 15px !important;
        margin-bottom: -10px !important;
        background: url(http://smalandsgymnasiet.com/wp-content/themes/smalandsgymnasiet/bilder/logo.png) no-repeat !important;
    }
    #nav {
        background-color: white !important;
    }
    .nav-top-item-link, .nav-top-item, #nav {
        color: #2c2c2c !important;
        font-style: normal !important;
    }
    .nav-sub-item-link, .action-section, .table-tag-wrapper, .box, #content-frame-wrapper, .dialog, .entry, #blog .comments-list {
        border-radius: 0 !important;
    }
    #nav .nav-top-item:hover, #nav .nav-top-item:focus, #nav .nav-top-item:active, #nav .nav-top-item.active {
        background: #2c2c2c !important;
    }
    #nav .nav-top-item:hover .nav-top-item-link, #nav .nav-top-item:focus .nav-top-item-link, #nav .nav-top-item:active .nav-top-item-link, #nav .nav-top-item.active .nav-top-item-link {
        color: #FFFFFF !important;
    }
    #headerOuter {
        margin-bottom: 1em !important;
    }
    #bread-crumbs, #toolbox-area, .box-header, #nav-top-courses, #nav-top-personal, #nav-top-communication, #nav-top-support {
        display: none !important;
    }
    #nav-top-tools {
        float: right !important;
    }
    #nav-top-tools.nav-top .nav-sub {
        left: auto !important;
        right: 0 !important;
        text-align: right !important;
    }
    #box-body-SkolplatsenStudent h3 {
        display: none !important;
    }
    .a-block li:hover, .discuss-overview-list ul li:hover, .a-block li:focus, .discuss-overview-list ul li:focus, #contentNodeTree .row:hover, #contentNodeTree .row:focus, #contentNodeTree .row table:hover, #contentNodeTree .row table:focus, .selection-list-box .row:hover, .selection-list-box .row:focus, .menu .menuItem:hover, .menu .menuItem:focus, .portfolio .row:hover, .portfolio .row:focus {
        background-color: #E1E1E1 !important;
        border-radius: 0 !important;
        color: #4B4B4B !important;
    }
    .icon-course-small, .icon-noticeboard, .icon-shortcut-small, .icon-pim-online, .icon-latest-news-small, .icon-lessons-small, .icon-files-small, .icon-reportsubmissions-small, .icon-calendar-small, .icon {
        background-image: none !important;
    }
	.icon-envelope-open-small {
  		background-image: url("https://karloskar.pingpong.se/pp/lookAndFeel/standard/icons/envelope_open_small.png") !important;
  	}
    .a-block li.icon a, .discuss-overview-list ul li.icon a {
        padding-left: 5px !important;
    }
    #nav .nav-sub-item-link:active, #nav .nav-sub-item-link:focus, #nav .nav-sub-item-link:hover, #nav .nav-sub-item-link.hovered {
        background-color: #2c2c2c !important;
        color: white !important;
    }
    #message-central-area {
        font-size: 12px !important;
        margin-right: 7px !important;
    }
    #message-central-area a:hover {
        background-color: #2c2c2c !important;
        color: white !important;
    }
    #main-header-top {
        position: absolute !important;
        right: -35px !important;
        top: 30px !important;
        text-transform: capitalize !important;
    }
    #nav {
        position: absolute !important;
        right: 120px !important;
        top: 49px !important;
        padding: 0 0 0 0 !important;
    }
    .icon-latest-news-small, .icon-contact-small {
        display: none !important;
    }
    #EntranceMessage b {
        font-weight: normal !important;
    }
    #pimInboxTable a {
        text-decoration: none !important;
    }
    .default-table th {
        background: #F2F2F2 !important;
    }
    .default-table td {
        padding: 0.5em 0 0.5em 0.5em !important;
    }
    .nowrap label {
        display: none !important;
    }
    .box-border, .leftCell, .border-left, .border-right {
        border: 0 !important;
    }
    .leftCell {
        text-align: left !important;
    }
    .page-content-header, .one-column-content p {
        display: none !important;
    }
    .table-tag-wrapper {
       padding: 0 !important;
    }
    .default-table {
        border: 0 !important;
    }
    .page-iterator {
        padding: 1em 1em 0 !important;
    }
    .page-iterator a {
        color: black !important;
        text-decoration: none !important;
    }
    .page-iterator .float-left {
        padding-top: 3px !important;
    }
    .selected {
        background-color: #e1e1e1 !important;
        border-radius: 0 !important;
    }
    .selected a {
        color: black !important;
    }
    .cal-menu-box .selected a {
        background-color: #e1e1e1 !important;
    }
    .cal-mini a {
        border-radius: 0 !important;
    }
    input[type="text"], input[type="password"], input[type="number"], textarea {
       border-radius: 0 !important;
       margin: 0 !important;
       padding: 0.6em !important;
       width: 100% !important;
    }
    .button {
        border-radius: 0 !important;
    }
    .tabContainer .tab, .calendarTabContainer .tab {
        border-radius: 0 !important;
    }
    .tabPageContainer, .calendarPageContainer {
        border-radius: 0 !important;
        padding: 1em !important;
    }
    .table-view th {
        background: #F9F9F9 !important;
    }
    .schema-header {
        display: none !important;
    }
    .action-section a:hover, #contentNodeTree a:hover, .rte-nav a:hover, .rte-nav-bottom a:hover, .selection-list-box a:hover, #EntranceMessage a:hover, #ContactList a:hover, #ProjectGroups a:hover, .podcast-start a:hover, .link-set a:hover, .default-table td a:hover {
        color: #ffbb40;
    }
    .icon-pim-online {
        padding: 0.8em 1.5em !important;
    }
    #main-header-top ul {
        margin: 0 !important;
        margin-right: 13px !important;
        float: none !important;
    }
    .icon-pim-offline {
       padding: 0.8em 1.5em !important;
    }
    #Wall .separator {
        border-bottom: 1px dotted grey;
        margin-bottom: 5px;
    }
    #Wall .separator:last-child {
        border: 0;
    }
}

@-moz-document url('https://karloskar.pingpong.se/startPage.do') {
    .right-column hr, .right-column li:last-child {
       display: none !important;
    }
}

@-moz-document url('https://karloskar.pingpong.se/pimListInbox.do'), url('https://karloskar.pingpong.se/pimListOutbox.do'), url('https://karloskar.pingpong.se/pimWrite.do') {
    #nav-top-communication {
        display: block !important;
    }
}
