css:
@-moz-document domain("se7ensins.com") {
img[src*="logo_bg"]{
    width:0;
    height:150px;
    padding-right:375px;
    background:transparent url(http://i.imgur.com/EBV5SLY.png) top left no-repeat;
}
/*Modbar and reports*/
#moderatorBar {
font-size: 11px;
background-color: #FF4294;
padding-top: 5px;
padding-bottom: 5px;
border-bottom: 1px solid #FF4294;
}
#moderatorBar a:hover {
color: #f3f3f3;
text-decoration: none;
background-color: 
#FF006E;
}
#moderatorBar .itemCount.alert {
color: white;
background-color: rgb(83, 83, 241);
-webkit-box-shadow: none;
-moz-box-shadow: none;
-khtml-box-shadow: none;
box-shadow: none;
}
/*Navigation and tabs*/
.navTabs {
background-color: #FF4294;
padding-top: 5px;
border-bottom: 5px solid #FF006E;
}
.navTabs .navTab.PopupClosed .navLink {
color: #FFFFFF;
}
.navTabs .navTab.selected {
background: linear-gradient(to bottom,rgba(255, 202, 225, 1) 0%,rgba(255,0,110,1) 100%);
}
.navTabs .navTab.PopupOpen .navLink:hover, .navTabs .navTab.PopupClosed .navLink:hover {
border-top-left-radius: 3px;
border-top-right-radius: 3px;
background: linear-gradient(to bottom,rgba(255, 202, 225, 1) 0%,rgba(255,0,110,1) 100%);
}
.navTabs .navTab.PopupOpen {
-webkit-border-top-left-radius: 3px;
-moz-border-radius-topleft: 3px;
-khtml-border-top-left-radius: 3px;
border-top-left-radius: 3px;
-webkit-border-top-right-radius: 3px;
-moz-border-radius-topright: 3px;
-khtml-border-top-right-radius: 3px;
border-top-right-radius: 3px;
background: -moz-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(90,157,50,1)),color-stop(100%,rgba(82,124,57,1)));
background: -webkit-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -o-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -ms-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: linear-gradient(to bottom,rgba(255, 202, 225, 1) 0%,rgba(255,0,110,1) 100%);
}
                        /*header*/
#logoBlock {
background: #1a1a1a url('http://i.imgur.com/czZckb7.png');
}
  html {
background-color: #FF68A9;
overflow-y: scroll!important;
}
                    #userBox {
padding: 6px 9px 3px 6px;
margin: 6px;
background-color: rgba(255, 104, 169, 0.15);
border: 2px solid #FF68A9;
float: right;
}
  .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt {
color: rgb(0, 0, 0);
}
  .nodeList .categoryStrip {
font-size: 11px;
color: #689f10;
padding: 5px 10px;
margin: 3px auto 0;
border-top: 1px solid #d0d0d0;
border-bottom: 1px solid #d0d0d0;
background: -moz-linear-gradient(top,rgba(224,224,224,1) 0%,rgba(216,216,216,1) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(224,224,224,1)),color-stop(100%,rgba(216,216,216,1)));
background: -webkit-linear-gradient(top,rgba(224,224,224,1) 0%,rgba(216,216,216,1) 100%);
background: -o-linear-gradient(top,rgba(224,224,224,1) 0%,rgba(216,216,216,1) 100%);
background: -ms-linear-gradient(top,rgba(224,224,224,1) 0%,rgba(216,216,216,1) 100%);
background: linear-gradient(to bottom,rgba(224,224,224,1) 0%,rgba(216,216,216,1) 100%);
padding: 10px;
margin: 0;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
background: -moz-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(90,157,50,1)),color-stop(100%,rgba(82,124,57,1)));
background: -webkit-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -o-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -ms-linear-gradient(top,rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: linear-gradient(to bottom,rgba(255, 134, 186, 1) 0%,rgba(255,0,110,1) 100%);
}
  .discussionList .sectionHeaders {
padding-top: 5px;
padding-bottom: 5px;
margin-top: 0px;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
background: -moz-linear-gradient(top, rgba(90,157,50,1) 0%, rgba(82,124,57,1) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(90,157,50,1)), color-stop(100%,rgba(82,124,57,1)));
background: -webkit-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -o-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -ms-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: linear-gradient(to bottom,rgba(255, 134, 186, 1) 0%,rgba(255,0,110,1) 100%);
}
.discussionList .sectionHeaders, .discussionListItem {
_vertical-align: bottom;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
background-image: url('http://i.imgur.com/Tp6qpfT.png');
background-repeat: no-repeat;
background-position: -36px 0;
}
.primaryContent a {
color: #FF3A89;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
font-weight: bold;
font-size: 12px;
color: #FF3A89;
background-color: #1a1a1a;
padding: 10px;
margin: -5px -5px 5px;
border-bottom: 3px solid #FF3A89;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
}
a:link, a:visited {
color: #FF3A89;
}
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
font-weight: bold;
font-size: 12px;
color: #FF3A89;
}
.sidebar .section .secondaryContent .footnote {
color: #FF1070;
}
.messageHeader {
padding: 10px;
margin-bottom: 5px;
margin-left: 0px;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
background: -moz-linear-gradient(top, rgba(90,157,50,1) 0%, rgba(82,124,57,1) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(90,157,50,1)), color-stop(100%,rgba(82,124,57,1)));
background: -webkit-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -o-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: -ms-linear-gradient(top, rgba(90,157,50,1) 0%,rgba(82,124,57,1) 100%);
background: linear-gradient(to bottom,rgba(255, 134, 186, 1) 0%,rgba(255,0,110,1) 100%);
}
.message .messageMeta .control:hover {
color: #fff;
text-decoration: none;
background-color: #FF1070;
}
.button:hover, .button[href]:hover, .buttonProxy:hover .button {
color: #fff;
text-decoration: none;
background-color: #FF1070;
border-color: #FF1070;
}
.button {
font-style: normal;
font-size: 12px;
font-family: Calibri,'Trebuchet MS',Verdana,Geneva,Arial,Helvetica,sans-serif;
color: #fff;
background-color: #FF7E93;
padding: 0px 6px;
border: 1px solid #FF1070;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
text-align: center;
outline: none;
line-height: 23px;
display: inline-block;
cursor: pointer;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
-ms-box-sizing: border-box;
box-sizing: border-box;
height: 23px;
}
.button.primary {
background-color: #FF7E93;
}
a.callToAction {
background-color: #FF7E93;
padding: 2px;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
display: inline-block;
line-height: 20px;
outline: 0 none;
height: 20px;
}
#SignupButton:hover, a.callToAction:hover {
background-color: #FF1070;
}
.textHeading, .xenForm .sectionHeader {
font-weight: bold;
color: #FF3C8A;
padding-bottom: 2px;
margin: 10px auto 5px;
border-bottom: 1px solid #d0d0d0;
}
#userBox .navLink .itemCount {
font-weight: bold;
font-size: 9px;
color: white;
background-color: rgb(83, 83, 241);
padding: 0 2px;
-webkit-border-radius: 2px;
-moz-border-radius: 2px;
-khtml-border-radius: 2px;
border-radius: 2px;
position: absolute;
right: 2px;
top: -12px;
line-height: 16px;
min-width: 12px;
_width: 12px;
text-align: center;
text-shadow: none;
white-space: nowrap;
word-wrap: normal;
-webkit-box-shadow: 2px 2px 5px rgba(0,0,0, 0.25);
-moz-box-shadow: 2px 2px 5px rgba(0,0,0, 0.25);
-khtml-box-shadow: 2px 2px 5px rgba(0,0,0, 0.25);
box-shadow: 2px 2px 5px rgba(0,0,0, 0.25);
height: 16px;
}
.profilePage .followBlocks .section .subHeading.textWithCount {
font-weight: bold;
font-size: 12px;
color: #FF3A89;
background: #1a1a1a none;
padding: 10px;
border-style: none;
border-bottom: 3px solid #FF3A89;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-khtml-border-radius: 3px;
border-radius: 3px;
}
.profilePage .followBlocks .section .subHeading.textWithCount .count {
color: #f3f3f3;
background-color: #FF3A89;
border-color: #FF3A89;
}
.textWithCount.subHeading .text {
color: #FF3A89;
}
}
