    background: #181818 none repeat scroll left top !important;
}
div#middle textarea {
    background: #181818 none repeat scroll 0 0 !important;
    color: #EEEEEE !important;
}

div.notice {
    background: #222 url(data:image/png;base64,) no-repeat scroll 0 50% !important;
    color: gold;
}

