    background: #333 url(data:image/gif;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAARCAYAAAA7bUf6AAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAAPYQAAD2EBqD+naQAAAAd0SU1FB9kCFRcQDcn4yJMAAAAZdEVYdENvbW1lbnQAQ3JlYXRlZCB3aXRoIEdJTVBXgQ4XAAAANUlEQVQ4y2Osq6v7z0AhYDl79iwDxYZ8//59kBjy69cvyg359+8fxYYwMVABjBoyasjQMgQAjewQvt1RxQMAAAAASUVORK5CYII=) !important;
