background:
 url(data:image/gif;base64,R0lGODlhBAATAMIGACELCyMLCysODiwODi4PDy8PD////////yH5BAEKAAcALAAAAAAEABMAAAMTKHBXNc0FVwi9OOvNsZUONCxHAgA7) no-repeat scroll left top !important;
