    border:1px solid orange !important;
}

div#sidebar div.transparent-box-rounding {
background:transparent url(data:image/png;base64,) no-repeat scroll left top !important; }

div#sidebar div.transparent-box {
background:transparent url(data:image/png;base64,) repeat scroll left top !important; 
}

div#sidebar div.transparent-box-rounding-bottom {
background:transparent url(data:image/png;base64,) no-repeat scroll left top !important;
}


div#sidebar div#profile-info dl dt.small {
    color: white !Important;
    font-weight:bold;
    letter-spacing:1px !important;
}
div#sidebar div#profile-info dl dd.small {
    color: silver !Important;
}

div#sidebar div.transparent-box ul.tab-bar li h2.single, div#sidebar div.transparent-box ul#tab-bar li h2.single {
background:transparent url(data:image/png;base64,) no-repeat scroll center top !Important;
}

