    background:transparent url(data:image/png;base64,) no-repeat scroll center top !important;
}

.login-data, .static-links a {
    color: #444 !important;
}
.static-links a:hover {
    color: #E60 !important;
}
.static-links a b, .login-data a {
    color: #E60 !important;
}

.container .content a, #sidebar a {
    color: #39F !important
}

div#content-full div.bliposphere span.status span.body {
    background: #333 !important;
    border: none !important;
    color: #eee;
}

div.bliposphere {
    background: transparent url(data:image/png;base64,) repeat scroll left top !important;
}

div.bliposphere-rounding-top {
   background-image: url(data:image/png;base64,) !important
}

div.bliposphere-rounding-bottom {
    background-image: url(data:image/png;base64,) !important;
}


#login-form strong, #remember label {
  color: black !important;
}
#login-form input {
