css:
@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google") {
body, html {
background: url(http://fc01.deviantart.net/fs71/f/2012/074/a/c/pyramid_pony_by_jamey4-d4sw1w7.png) fixed no-repeat !important;
  background-size: cover !important;
font-size: small;
}

.fbar {
background: rgba(242, 242, 242, 0) !important;
border-top: 1px solid rgba(228, 228, 228, 0) !important;
line-height: 40px;
min-width: 980px;
}

#hplogo, #hplogoy, #hplogoc {
height: 0px !important;
outline: none;
width: 391px;
-moz-user-select: -moz-none;
-ms-user-select: none;
-webkit-tap-highlight-color: transparent;
-webkit-user-select: none;
}

.gbqfqw {
background: rgba(255, 255, 255, 0) !important;
background-clip: padding-box;
border: 1px solid #FF0000 !important;
border-color: rgba(143, 0, 0, 1) !important;
border-right-width: 0;
height: 30px;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

.gb_Ab {
background: rgba(241, 241, 241, 0) !important;
}

.gbqfif, .gbqfsf {
color: #F00 !important;
font: 16px arial,sans-serif;
}

.gssb_m {
color: #000;
background: rgba(255, 255, 255, 0) !important;
}

.gssb_e {
border: 1px solid rgba(204, 204, 204, 0) !important;
border-top-color: rgba(255, 0, 0, 0) !important;
box-shadow: 0 2px 4px rgba(255, 0, 0, 0.14) !important;
-webkit-box-shadow: 0 2px 4px rgba(255, 0, 0, 1) !important;
cursor: default;
}

.gssb_i {
background: rgba(255, 0, 0, 0.11) !important;
}

a.gssb_j {
font-size: 13px;
color: #F00 !important;
text-decoration: none;
line-height: 100%;
}

.gsri_a {
background: none !important;
background-size: 14px 19px;
display: inline-block;
height: 23px;
width: 17px;
}

#gbqfb {
border: none;
border-bottom-left-radius: 0;
border-top-left-radius: 0;
height: 30px;
outline: none;
padding: 0 0;
width: 60px;
-webkit-box-shadow: none;
box-shadow: none;
background: #2B3950;
background: -webkit-linear-gradient(top,#FF0000,#520000);
background: linear-gradient(top,#4387fd,#4683ea) !important;
}

.gb_va {
background-repeat: no-repeat;
  background-size: 0px !important;
}

#hdtbSum {
background: rgba(255, 255, 255, 0) !important;
border-bottom: 1px solid rgba(235, 235, 235, 0) !important;
height: 40px;
line-height: 35px;
padding: 0;
position: relative;
z-index: 102;
}

#appbar {
background: rgba(255, 255, 255, 0) !important;
-webkit-box-sizing: border-box;
width: 100%;
}

.vk_c, .vk_cxp {
background-color: rgba(255, 0, 0, 0.29) !important;
position: relative;
}

#hdtb {
background: rgba(255, 255, 255, 0) !important;
color: #666;
outline-width: 0;
outline: none;
position: relative;
z-index: 102;
}

span[style*="color: rgb(82, 24, 140);"]{
  color: #B90000 !important;
}

.ksb.ksbs, .ksb.ksbs:hover, .ab_button.selected, .ab_button.selected:hover {
background-color: #FFF;
background-image: -webkit-gradient(linear,left top,left bottom,from(#eee),to(#e0e0e0));
background-image: -webkit-linear-gradient(top,#FF0000,#520000) !important;
background-image: linear-gradient(top,#eee,#e0e0e0);
border: 1px solid rgba(255, 0, 0, 1) !important;
-webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
color: #222;
margin: 0;
}

.ksb, .ab_button {
background-color: #FF0000;
background-image: -webkit-gradient(linear,left top,left bottom,from(#f5f5f5),to(#f1f1f1));
background-image: -webkit-linear-gradient(top,#FF0000,#520000) !important;
background-image: linear-gradient(top,#f5f5f5,#f1f1f1);
border: 1px solid #dcdcdc;
border: 1px solid rgba(255, 0, 0, 1) !important;
color: #444;
}

.kno-ecr-pt {
color: #F00 !important;
font-size: 30px;
font-weight: normal;
}

.knop {
color: #A30000 !important;
}

.scrt tr:first-child td {
border-top: solid 1px rgba(235, 235, 235, 0) !important;
}

.scrt td {
border-bottom: solid 1px rgba(255, 0, 0, 0) !important;
}

.vk_arc {
border-top: 1px solid rgba(235, 235, 235, 0) !important;
cursor: pointer;
height: 0px;
margin-bottom: -23px;
overflow: hidden;
padding: 20px 0;
text-align: center;
}

a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
color: #FF0000 !important;
}

.mblink:visited, a:visited {
color: #9B0000 !important;
}

.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #ans>i, .bc a:link {
color: #000000 !important;
font-style: normal;
}

.s {
color: #F00 !important;
}

.crl {
color: #FFF !important;
cursor: pointer;
display: inline-block;
}

.kno-ftr {
color: #F00 !important;
font-size: 11px;
line-height: normal;
}

.scrt th {
color: #FFF !important;
font-weight: bolder;
text-align: left;
}

.f, .f a:link, .m, .descbox {
color: #FFF !important;
}

._yn, ._yn a.fl {
color: #FFF !important;
margin-top: 3px;
}

.csb, .ss, .micon, .close_btn, #tbp, .mbi {
background: none !important;
overflow: hidden;
}

body {
color: #970000 !important;
}

#resultStats {
color: #F00 !important;
font-size: 13px;
line-height: 35px;
overflow: hidden;
text-overflow: ellipsis;
white-space: nowrap;
}

#hdtb_msb #hdtb_more, #hdtb_msb #hdtb_tls {
color: #FF0000 !important;
}

.mn-dwn-arw {
border-color: #FF0000 transparent !important;
border-style: solid;
border-width: 4px 4px 0 4px;
width: 0;
height: 0;
margin-left: -2px;
top: 50%;
margin-top: -2px;
position: absolute;
}

#hdtbMenus {
background: rgba(0, 0, 0, 0) !important;
padding-bottom: 5px;
padding-top: 7px;
top: 0;
width: 100%;
height: 22px;
position: absolute;
transition: top 220ms ease-in-out;
-webkit-transition: top 220ms ease-in-out;
}

.hdtb-mn-hd {
color: #810000 !important;
line-height: 22px;
cursor: pointer;
}

.vk_c, .vk_cxp, #rhs .vk_rhsc {
-webkit-box-shadow: 0 1px 4px rgba(255, 0, 0, 1) !important;
box-shadow: 0 1px 4px rgba(255, 0, 0, 1) !important;
}

#hdtb_msb .hdtb-tl-sel, #hdtb_msb .hdtb-tl-sel:hover {
background: -webkit-linear-gradient(top,#FF0000,#520000) !important;
-webkit-box-shadow: inset 0 1px 2px 0 rgba(0, 0, 0, 1) !important;
border: 1px solid #FF0000;
box-shadow: inset 0 1px 2px 0 rgba(0, 0, 0, 0) !important;
margin-left: 9px;
}

.lhshdr, .tphdr, #tbbcc, .rshdr {
background: rgba(255, 255, 255, 0) !important;
}

#cnt {
background-color: rgba(241, 241, 241, 0) !important;
}

#res li.psgi {
border: rgba(235, 235, 235, 0) solid 1px !important;
display: inline-block;
font-size: 13px;
margin: 0;
margin-top: -1px;
margin-right: -1px;
padding: 20px 5px 14px;
position: relative;
vertical-align: top;
width: 180px;
z-index: 0;
color: #666;
}

._dc {
color: red !important;
}

.GJJKPX2A-B {
background: rgba(246, 246, 246, 0) !important;
width: 650px;
padding: 6px 6px 0;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

.GJJKPX2INC, .GJJKPX2GNC {
color: #F00 !important;
}

.GJJKPX2OMC {
white-space: nowrap;
border-bottom: 1px solid rgba(0, 0, 0, 0) !important;
}

}
