css:
@-moz-document domain(zongheng.com) {
	body
	{
	    background: none!important;
	}
	.logonbox
	{
	    position: absolute;
	}
	.zonghengbg
	{
	    padding-top: 44px!important;

	    background: none!important;
	}
	.zonghengbg .wrap
	{
	    border: 1px solid #ccc;
	}
	.zonghengbg .wrap .secondary{
		padding: 10px 0;
	}

	#backToTop{
		bottom: 20px;
	}

	.userImage{
		visibility: hidden;
	}

	.news .box{
		height: 484px;
	}
	.news .news_one{
		height: 420px;
	}
	.menu{
		background: #f8f6f1;
		border-bottom: 1px solid #ccc;
	}
		/*旧版*/
	.newlink,
		/*充值*/
		.default_wrap,
		/*注册*/
		.regist,
		/*导航*/
		#ui_top_nav_button,
		/*阅读历史*/
		#read_history,
		/*搜索框*/
		.searchbar,
		/*关闭链接*/
		#top_bar_close,
		/*黑线*/
		.blackline,
		.zonghengbg .wrap .nav_left,
		.zonghengbg .wrap .cl10,
		/*美女秀场*/
		.zonghengbg .wrap .secondary .girl,
		/*充值*/
		.menu .money,
		/*游戏中心*/
		.menu a.gamecenter,
		/*空白框*/
		.top_gg,
		/*空白框*/
		.kind_recom .tab_card span,
		.news_one h2:nth-of-type(n+2),
		.news_one ul.wz:nth-of-type(n+2),
		#tpad,
		/*女生/出版隐藏*/
		.tab_card .tabtitle:nth-child(n+2),
		/*游戏news*/
		.news .imgbox,
		/*客服*/
		#sidekf,
		/*二维码隐藏*/
		#zherweima,
		/*合作伙伴*/
		.wrap .partner,
		/*你提我改*/
		#tips,
		/*相关*/
		.wrap.foot,
		/*广告*/
		embed,
		/*一些图标*/
		.kind_recom span.hot,
		.two_main .title em,
		.main_con .line:nth-of-type(3) + li
	{
	    display: none!important;
	}
}
