css:
@-moz-document url-prefix("https://www.azlyrics.com/"), url-prefix("http://www.azlyrics.com/") {
 
    body { 
        background: #282828 url('https://31.media.tumblr.com/d2794cad167eaf71f5e2695cf3d07cdb/tumblr_inline_n1r5c46tLf1qmpver.jpg') top center repeat-x fixed;
        font-family: Calibri, sans serif;
        font-size: 1.4em; 
        line-height: 1.4em;
    }
    h1 { color: #ffffff; }
    a, a:focus { color: #101010; transition: all .5s ease; }
    a:hover, a:active { color: #808080; text-decoration: none; }
    .navbar-brand {
        width: 160px;
        height: 85px;
        padding: 0;
        margin: -32px 0 0 0;
        background: url('https://31.media.tumblr.com/3e0b4588a97d6f06f2eab2fc850c00a9/tumblr_inline_n1tpqjAad21qmpver.png');
    }
    .container { width: 80%; }
    .menu, .smenu, .copyright { background: #a0a0a0; }
    .copyright { padding: 0 0 10px 0; }
    .col-lg-8, .col-md-6 { background: #c0c0c0; border: 4px solid #e0e0e0; } 
    .listrow { background: #232323; }
    .sites, .sites a { color: #151515; }
    #inn { background: #e0e0e0; }
    #inn a, .smenu a { color: #151515; font-weight: bold; }
    .comment { color: #353535; }
    #inn iframe { visibility: hidden !important; }
    a.extLink { color: #c90000 !important; }
    #inn a:visited { color: #808080; }
    .bmenu { visibility: visible; padding: 0 0 10px 0; }
    .bmenu a { color: #151515; }
    .addthis_default_style, .noprint { visibility: hidden; height: 10px; width: 100%; }
    .smallfont { padding: 10px; }
    .main-page { width: 75%; margin: auto; }
    .col-lg-8, .col-md-6 { width: 80%; margin: 0 auto 0 auto; }
    .col-lg-2, .col-md-3 { width: 10%; margin: auto; }
    .navbar-default { background: #808080; border: none; }
    .lboard-wrap { padding: 7px; background: #f0f0f0; }
    .btn-group .btn { margin: 0 4px 0 3px; background: #101010; border-color: #101010; transition: all .5s ease; }
    .btn-group .btn:hover { background: #303030; border-color: #303030; color: #ffffff; }
    .input-group-btn:last-child > .btn { background: #151515; border-color: #151515; }
}
