css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mwo.smurfy-net.de") {
.label-mech-hardpoint-ecm {
background-color: rgb(0, 235, 235);
}
.slot.slot-data-module-cgecmstats {
color: rgb(0, 235, 235);
border-color: rgb(0, 235, 235);
}
.new-component-item.new-component-item-ams {
border-color: rgb(255, 255, 255);
}
.slot.slot-data-weapon-ams {
color: rgb(255, 255, 255);
border-color: rgb(225, 225, 225);
}
.label-mech-hardpoint-ams {
background-color: rgb(255, 255, 255);
}
.new-component-item.new-component-item-ballistic {
border-color: rgb(255, 245, 110);
}
.slot.slot-data-weapon-ballistic {
color: rgb(255, 245, 110);
border-color: rgb(255, 245, 110);
}
.label-mech-hardpoint-ballistic {
background-color: rgb(255, 245, 110);
}
.slot.slot-data-weapon-missle {
color: rgb(60, 185, 120);
border-color: rgb(60, 185, 120);
}
.label-mech-hardpoint-missle {
color: rgb(255, 255, 255);
background-color: rgb(60, 185, 120);
}
.slot {
background: none repeat scroll 0 0 #202020;
border: 1px solid #716604;
}
.component-title {
background-image: linear-gradient(to bottom, rgb(113, 102, 4), rgb(190, 171, 7));
border: 0px;
}
.component-restrictions {
background-image: linear-gradient(to bottom, rgb(190, 171, 7), rgb(113, 102, 4));
border: 0px;
}
.slot-internal {
color: rgb(105, 95, 17);
}
.slot-dynamic {
color: rgb(135, 135, 135);
}
.slot-free {
color: rgb(85, 85, 85);
}
.new-component-item.new-component-item-beam {
border-color: rgb(242, 108, 79);
}
.slot.slot-data-weapon-beam {
color: rgb(240, 110, 80);
border-color: rgb(240, 110, 80);
}
.label-mech-hardpoint-beam {
color: rgb(255, 255, 255);
background-color: rgb(240, 110, 80);
}
.label.armor-values {
background-color: rgb(70, 70, 70);
border: 0px;
}
}
