css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phoenix.benetrac.com"), domain("vali.benetrac.com"), domain("cronus.benetrac.com"), domain("triton.benetrac.com") {

/*Max Width*/
.sui-fluid-container { margin-top: 0px!important; }
.sui-fluid-container { max-width: 9999999px!important; }
.sui-navbar-bg .fill { max-width: 9999999px!important; }
.sui-navsections { max-width: 9999999px!important; }

/*Get rid of sidebar and ad blocks*/
#content_wrapper {padding-right: 0px;}
#sidebar {display:none;}
#adbox{display:none;}
 
/*tighten up the header rows*/
.sui-opt-in .table td{padding:2px !important;}
.sui-opt-in .helpdesk-root .ticket-grid .table-body .ticket-table-wrapper td {line-height:13px !important;}
 
/*tighten up the detail header*/
.sui-opt-in .helpdesk-root .ticket-pane .ticket-header {padding: 4px 30px 2px 120px;}
.sui-opt-in .helpdesk-root .ticket-pane .ticket-ribbon {padding: 1px 30px 1px;}
 
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .actions-area {margin-bottom: 0px;}
 
#application_messaging {display:none;}
.sui-opt-in.page-header {margin:0px;}
.sui-body {padding: 50px 25px 25px 25px;}
 
/*Control public comments*/
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .response .activity-event.contents-overflows .body-wrapper .bottom-mask{background-image: none;}
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .response .activity-event .body-wrapper p {max-height: none;}
 
/*Control private notes*/
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .note .activity-event.contents-overflows .body-wrapper .bottom-mask{background-image: none;}
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .note .activity-event .body-wrapper p {max-height: 18px;}
 
/*Control labor records*/
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .labor .activity-event.contents-overflows .body-wrapper .bottom-mask{background-image: none;}
.sui-opt-in .helpdesk-root .ticket-pane .tab-sections .tab-pane.activity .activity-feed .labor .activity-event {display:none;}

}
